import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, ActivityIndicator, ScrollView } from "react-native";
import { fetchAllPersons } from "../Application/personService"

const Persons = () => {
    const [persons, setPersons] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const loadPersons = async () => {
            try {
                const allPersons = await fetchAllPersons(); // Abrufen der Personenliste
                setPersons(allPersons);
                console.log
            } catch (err) {
                console.error("Fehler beim Laden der Personen:", err);
                setError("Daten konnten nicht geladen werden.");
            } finally {
                setLoading(false);
            }
        };

        loadPersons();
    }, []);

    if (loading) {
        return (
            <View style={styles.loader}>
                <ActivityIndicator size="large" color="#f0af4d" />
                <Text>Daten werden geladen...</Text>
            </View>
        );
    }

    if (error) {
        return (
            <View style={styles.errorContainer}>
                <Text style={styles.errorText}>{error}</Text>
            </View>
        );
    }

    return (
        <ScrollView style={styles.container}>
            {persons.map((person, index) => (
                <View key={index} style={styles.personContainer}>
                    <Text style={styles.name}>
                        {person.vorname} {person.nachname} ({person.typ})
                    </Text>
                    <Text style={styles.details}>Email: {person.email}</Text>
                    <Text style={styles.details}>Telefon: {person.telefon}</Text>
                    <Text style={styles.details}>Adresse: {person.adresse}</Text>
                </View>
            ))}
        </ScrollView>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20, backgroundColor: "#fff" },
    loader: { flex: 1, justifyContent: "center", alignItems: "center" },
    errorContainer: { flex: 1, justifyContent: "center", alignItems: "center" },
    errorText: { color: "red", fontSize: 16 },
    personContainer: {
        padding: 15,
        borderBottomWidth: 1,
        borderColor: "#ddd",
    },
    name: { fontWeight: "bold", fontSize: 16 },
    details: { color: "gray", fontSize: 14 },
});

export default Persons;
