import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { View, Text, StyleSheet } from 'react-native';

const PersonDetail = ({ persons }) => {
    const { id } = useParams(); // ID aus der URL
    const [person, setPerson] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (!id) {
            setError('Keine ID in der URL gefunden.');
            return;
        }

        if (!persons || persons.length === 0) {
            setError('Die Personenliste ist leer oder nicht definiert.');
            return;
        }

        const foundPerson = persons.find((p) => p.personenNr?.toString() === id);
        if (!foundPerson) {
            setError(`Keine Person mit der ID ${id} gefunden.`);
        } else {
            setPerson(foundPerson);
        }
    }, [id, persons]);

    if (error) {
        return (
            <View style={styles.container}>
                <Text style={styles.error}>{error}</Text>
            </View>
        );
    }

    if (!person) {
        return (
            <View style={styles.container}>
                <Text style={styles.error}>Keine Details verf√ºgbar.</Text>
            </View>
        );
    }

    return (
        <View style={styles.container}>
            <Text style={styles.header}>Details zu {person.vorname} {person.nachname}</Text>
            <Text>Personen-Nummer: {person.personenNr}</Text>
            {/* Weitere Felder hier */}
        </View>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20, backgroundColor: '#fff', height: '100vh' },
    error: { color: 'red', fontSize: 20, textAlign: 'center' },
    header: { fontSize: 24, fontWeight: 'bold', marginBottom: 10 },
});

export default PersonDetail;
