import React from 'react';
import { useParams } from 'react-router-dom';
import { View, Text, StyleSheet } from 'react-native';
import { personList } from '../Application/Classes/testDataGenerator';

const PersonDetail = () => {
    const { id } = useParams();
    const person = personList.find(p => p.personenNr.toString() === id);

    if (!person) {
        return <Text style={styles.error}>Person nicht gefunden!</Text>;
    }

    return (
        <View style={styles.container}>
            <Text style={styles.title}>{person.vorname} {person.nachname}</Text>
            <Text>Personen-Nr: {person.personenNr}</Text>
            <Text>Betreuer: {person.betreuer}</Text>
            <Text>Sprache: {person.sprache}</Text>
        </View>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20 },
    title: { fontSize: 24, fontWeight: 'bold' },
    error: { color: 'red' }
});

export default PersonDetail;
