const API_BASE_URL = 'http://localhost:8080/api'; // Base URL of your backend

const getToken = async () => {
    try {
        const credentials = btoa("MAR200575:geheim1"); // Encode credentials
        const response = await fetch("http://localhost:8080/token", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Basic ${credentials}`,
            },
        });

        if (!response.ok) {
            throw new Error(`Failed to fetch token: ${response.statusText}`);
        }

        const token = await response.text(); // Assuming the token is a plain string
        if (!token) {
            throw new Error('Token is empty or invalid');
        }

        return token;
    } catch (error) {
        console.error('Error fetching token:', error);
        throw error;
    }
};

const fetchDataWithToken = async (endpoint, token) => {
    try {
        const response = await fetch(`${API_BASE_URL}${endpoint}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`, // Use Bearer token
            },
        });

        console.log(response.body);

        if (!response.ok) {
            throw new Error(`Error fetching data: ${response.statusText}`);
        }

        // Validate JSON response
        const contentType = response.headers.get('Content-Type');
        if (contentType && contentType.includes('application/json')) {
            return await response.json();
        } else {
            throw new Error('Invalid JSON response from the server');
        }
    } catch (error) {
        console.error('API Error:', error);
        throw error;
    }
};

/**
 * Performs a GET request to a given API URL.
 * @param {string} endpoint - The endpoint, e.g., "/persons".
 * @param {Object} [options={}] - Additional options for the fetch request.
 * @returns {Promise<Object>} - The response data in JSON format.
 * @throws {Error} - If the request fails or the response is not ok.
 */
export const fetchData = async (endpoint, options = {}) => {
    try {
        const token = await getToken();
        const data = await fetchDataWithToken(endpoint, token);

        return data;
    } catch (error) {
        console.error('API Error:', error);
        throw error;
    }
};

/**
 * Performs a POST request to a given API URL.
 * @param {string} endpoint - The endpoint, e.g., "/persons".
 * @param {Object} body - The payload (JSON object) for the POST request.
 * @param {Object} [options={}] - Additional options for the fetch request.
 * @returns {Promise<Object>} - The response data in JSON format.
 * @throws {Error} - If the request fails or the response is not ok.
 */
export const postData = async (endpoint, body, options = {}) => {
    try {
        const response = await fetch(`${API_BASE_URL}${endpoint}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(body),
            ...options,
        });

        if (!response.ok) {
            throw new Error(`Error sending data: ${response.statusText}`);
        }

        const data = await response.json();
        return data;
    } catch (error) {
        console.error('API Error:', error);
        throw error;
    }
};

/**
 * Performs a PUT request to a given API URL.
 * @param {string} endpoint - The endpoint, e.g., "/persons/{id}".
 * @param {Object} body - The payload (JSON object) for the PUT request.
 * @param {Object} [options={}] - Additional options for the fetch request.
 * @returns {Promise<Object>} - The response data in JSON format.
 * @throws {Error} - If the request fails or the response is not ok.
 */
export const updateData = async (endpoint, body, options = {}) => {
    try {
        const response = await fetch(`${API_BASE_URL}${endpoint}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(body),
            ...options,
        });

        if (!response.ok) {
            throw new Error(`Error updating data: ${response.statusText}`);
        }

        const data = await response.json();
        return data;
    } catch (error) {
        console.error('API Error:', error);
        throw error;
    }
};

/**
 * Performs a DELETE request to a given API URL.
 * @param {string} endpoint - The endpoint, e.g., "/persons/{id}".
 * @param {Object} [options={}] - Additional options for the fetch request.
 * @returns {Promise<void>} - Success message if deleted.
 * @throws {Error} - If the request fails or the response is not ok.
 */
export const deleteData = async (endpoint, options = {}) => {
    try {
        const response = await fetch(`${API_BASE_URL}${endpoint}`, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
            },
            ...options,
        });

        if (!response.ok) {
            throw new Error(`Error deleting data: ${response.statusText}`);
        }

        return 'Data successfully deleted.';
    } catch (error) {
        console.error('API Error:', error);
        throw error;
    }
