import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, Image, TouchableOpacity, TextInput, Modal, Button } from 'react-native';
import Nutzer from "./AlwaysActive/images/nutzer.png";

// Funktion, um zufällige Namen zu generieren
const generateRandomName = () => {
    const firstNames = [
        'Max', 'Anna', 'Liam', 'Sophia', 'Noah', 'Emma', 'Oliver', 'Ava', 
        'Ethan', 'Isabella', 'Lucas', 'Mia', 'Mason', 'Charlotte', 'Logan', 
        'Amelia', 'James', 'Harper', 'Jackson', 'Evelyn'
    ];

    const lastNames = [
        'Müller', 'Schmidt', 'Schneider', 'Fischer', 'Weber', 
        'Meyer', 'Wagner', 'Becker', 'Hoffmann', 'Schulz', 
        'Richter', 'Klein', 'Wolf', 'Schwarz', 'Zimmermann',
        'Braun', 'Krüger', 'Hofmann', 'Schmitz', 'Schulte'
    ];

    const firstName = firstNames[Math.floor(Math.random() * firstNames.length)];
    const lastName = lastNames[Math.floor(Math.random() * lastNames.length)];

    return { firstName, lastName };
};

// Generiere die Personenliste
const personsList = Array.from({ length: 100 }, () => generateRandomName());

const Persons = () => {
    const [searchText, setSearchText] = useState('');
    const [searchByFirstName, setSearchByFirstName] = useState(true);
    const [searchByLastName, setSearchByLastName] = useState(false);
    const [searchByBoth, setSearchByBoth] = useState(false);
    const [modalVisible, setModalVisible] = useState(false); // Steuerung des Modals

    // Funktion, um die Personenliste basierend auf den Suchkriterien zu filtern
    const filteredPersons = personsList.filter(person => {
        const fullName = `${person.firstName} ${person.lastName}`.toLowerCase();
        if (searchByFirstName) {
            return person.firstName.toLowerCase().includes(searchText.toLowerCase());
        } else if (searchByLastName) {
            return person.lastName.toLowerCase().includes(searchText.toLowerCase());
        } else if (searchByBoth) {
            return fullName.includes(searchText.toLowerCase());
        }
        return true; // Wenn keine Checkbox aktiv ist, zeigt alle Personen
    });

    return (
        <View style={styles.container}>
            <Text style={styles.welcomeText}>Personen</Text>

            {/* Suchleiste mit Dropdown-Filter */}
            <View style={styles.searchContainer}>
                <TextInput
                    style={styles.searchBar}
                    placeholder="Search Personen"
                    value={searchText}
                    onChangeText={text => setSearchText(text)}
                />
                <TouchableOpacity style={styles.filterButton} onPress={() => setModalVisible(true)}>
                    <Text style={styles.filterButtonText}>Filter</Text>
                </TouchableOpacity>
            </View>

            {/* Modal für die Filter-Checkboxen */}
            <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
                onRequestClose={() => setModalVisible(!modalVisible)}
            >
                <View style={styles.modalContainer}>
                    <View style={styles.modalContent}>
                        <Text style={styles.modalTitle}>Filter Optionen</Text>
                        <TouchableOpacity onPress={() => {
                            setSearchByFirstName(true);
                            setSearchByLastName(false);
                            setSearchByBoth(false);
                        }}>
                            <Text style={styles.modalOption}>Search by First Name</Text>
                        </TouchableOpacity>
                        <TouchableOpacity onPress={() => {
                            setSearchByFirstName(false);
                            setSearchByLastName(true);
                            setSearchByBoth(false);
                        }}>
                            <Text style={styles.modalOption}>Search by Last Name</Text>
                        </TouchableOpacity>
                        <TouchableOpacity onPress={() => {
                            setSearchByFirstName(false);
                            setSearchByLastName(false);
                            setSearchByBoth(true);
                        }}>
                            <Text style={styles.modalOption}>Search by Both</Text>
                        </TouchableOpacity>

                        <Button title="Schließen" onPress={() => setModalVisible(false)} />
                    </View>
                </View>
            </Modal>

            {/* Personenliste */}
            <ScrollView contentContainerStyle={styles.scrollViewContent}>
                <View style={styles.personGrid}>
                    {filteredPersons.map((person, index) => (
                        <View key={index} style={styles.personBox}>
                            <TouchableOpacity>
                                <View style={{ margin: 10 }}>
                                    <Image style={styles.images} source={Nutzer} />
                                </View>
                                <View style={{ backgroundColor: "lightgrey", height: 2, width: "100%", marginTop: 4, marginBottom: 4 }}></View>
                                <View>
                                    <Text style={styles.personName}>{person.firstName}</Text>
                                    <Text style={styles.personName}>{person.lastName}</Text>
                                </View>
                            </TouchableOpacity>
                        </View>
                    ))}
                </View>
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: '#fff',
        alignItems: 'flex-start',
    },
    welcomeText: {
        color: 'rgba(240,175,77,1)',
        fontWeight: "bold",
        fontSize: 50,
        marginBottom: 20,
    },
    searchContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 20,
        width: '100%',
    },
    searchBar: {
        flex: 1,
        padding: 10,
        borderColor: 'gray',
        borderWidth: 1,
        borderRadius: 5,
    },
    filterButton: {
        marginLeft: 10,
        padding: 10,
        backgroundColor: 'rgba(240,175,77,1)',
        borderRadius: 5,
    },
    filterButtonText: {
        color: '#fff',
    },
    modalContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'rgba(0, 0, 0, 0.5)', // Halbdurchsichtiges Hintergrund
    },
    modalContent: {
        width: '80%',
        backgroundColor: 'white',
        borderRadius: 10,
        padding: 20,
        alignItems: 'flex-start',
    },
    modalTitle: {
        fontSize: 18,
        fontWeight: 'bold',
        marginBottom: 20,
    },
    modalOption: {
        fontSize: 16,
        marginVertical: 10,
    },
    scrollViewContent: {
        alignItems: 'center',
    },
    personGrid: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
    },
    personBox: {
        width: '30%',
        height: 150,
        borderWidth: 1,
        borderColor: 'lightgray',
        borderRadius: 5,
        padding: 2,
        marginBottom: 10,
        alignItems: 'center',
        justifyContent: 'center',
    },
    personName: {
        fontSize: 14,
    },
    images: {
        width: 40,
        height: 40,
    },
});

export default Persons;
