import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const PersonDetail = ({ persons }) => {
    const { id } = useParams(); // ID aus der URL
    const [person, setPerson] = useState(null);

    const loadPerson = () => {
        if (!persons || persons.length === 0) {
            console.error("Die Liste der Personen ist leer oder nicht definiert.");
            return;
        }

        if (!id) {
            console.error("ID ist nicht definiert.");
            return;
        }

        const foundPerson = persons.find(p => p?.id?.toString() === id?.toString());

        if (!foundPerson) {
            console.error(`Keine Person mit der ID ${id} gefunden.`);
            return;
        }

        setPerson(foundPerson);
    };

    useEffect(() => {
        loadPerson();
    }, [id, persons]);

    if (!person) {
        return <p>Fehler beim Laden der Person. Überprüfen Sie die Konsole auf weitere Details.</p>;
    }

    return (
        <div>
            <h1>Details zu {person.vorname} {person.nachname}</h1>
            <p>Email: {person.email}</p>
            <p>Telefon: {person.telefon}</p>
            <p>Adresse: {person.adresse}</p>
            <p>Typ: {person.typ}</p>
        </div>
    );
};

export default PersonDetail;
