import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, ActivityIndicator, WebView } from "react-native";
import { LineChart, BarChart, PieChart } from "react-native-chart-kit"; // Diagrammtypen
import { fetchChartData } from "../Application/chartService";

// Importiere die HTML-Datei als Rohtext
import statistics from "../Components/Statistics/statistics.html";

const Graphs = () => {
    const [chartData, setChartData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchChartData()
            .then((data) => {
                setChartData(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Fehler beim Laden der Daten:", error);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <ActivityIndicator size="large" color="#0000ff" />;
    }

    return (
        <View style={styles.container}>
            <Text style={styles.header}>Datenvisualisierung</Text>

            {/* Anzeige der HTML-Statistiken */}
            <WebView
                originWhitelist={["*"]}
                source={{ html: statistics }}
                style={{ flex: 1, width: "100%", height: 500 }}
            />

            {/* Beispiel: Ein einfaches Liniendiagramm */}
            {chartData && (
                <LineChart
                    data={chartData}
                    width={300}
                    height={220}
                    yAxisLabel="â‚¬"
                    chartConfig={{
                        backgroundColor: "#e26a00",
                        backgroundGradientFrom: "#fb8c00",
                        backgroundGradientTo: "#ffa726",
                        decimalPlaces: 2,
                        color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
                        style: { borderRadius: 16 },
                    }}
                    bezier
                    style={styles.chart}
                />
            )}
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        padding: 20,
    },
    header: {
        fontSize: 20,
        fontWeight: "bold",
        marginBottom: 10,
    },
    chart: {
        marginVertical: 10,
        borderRadius: 16,
    },
});

export default Graphs;
