import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, Image, TouchableOpacity, TextInput, CheckBox } from 'react-native';
import { exponateList } from '../Application/Classes/testDataGenerator';  // Annahme: Diese Liste enthält Exponate
import listic from "./AlwaysActive/images/listic.png";
import Filter from "./AlwaysActive/images/filter.png";
import Squ4 from "./AlwaysActive/images/sq4.png";
import arrow from "./AlwaysActive/images/arrow.png";

const Exponate = () => {
    const [searchText, setSearchText] = useState('');
    const [searchByTitle, setSearchByTitle] = useState(true);
    const [showFilters, setShowFilters] = useState(false);
    const [changeListView, setListView] = useState(true);

    const filteredExponate = exponateList.filter(exponat => {
        const title = exponat.title?.toLowerCase();
        if (searchByTitle) {
            return title?.includes(searchText.toLowerCase());
        }
        return true;
    });

    return (
        <View style={styles.container}>
            <View style={{ flexDirection: "row", margin: 20 }}>
                <Text style={styles.welcomeText}>Exponate</Text>
            </View>

            <View style={styles.searchContainer}>
                <View style={styles.borders}>
                    <TouchableOpacity
                        style={[styles.filterButton, changeListView && styles.activeButton]}
                        onPress={() => setListView(true)}
                    >
                        <Image style={{ width: 20, height: 20 }} source={Squ4} />
                    </TouchableOpacity>
                </View>
                <View style={styles.borders}>
                    <TouchableOpacity
                        style={[styles.filterButton, !changeListView && styles.activeButton]}
                        onPress={() => setListView(false)}
                    >
                        <Image style={{ width: 20, height: 20 }} source={listic} />
                    </TouchableOpacity>
                </View>
                <View style={{
                    borderWidth: 1,
                    borderColor: 'gray',
                    borderRadius: 5,
                    flexDirection: "row",
                    width: 400,
                }}>
                    <TextInput
                        style={styles.searchBar}
                        placeholder="Search Exponate"
                        value={searchText}
                        onChangeText={text => setSearchText(text)}
                    />
                    <TouchableOpacity style={styles.filterButton} onPress={() => setShowFilters(!showFilters)}>
                        <Image style={{ width: 20, height: 20 }} source={Filter} />
                    </TouchableOpacity>
                </View>
            </View>

            {showFilters && (
                <View style={styles.dropdown}>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByTitle}
                            onValueChange={() => {
                                setSearchByTitle(!searchByTitle);
                            }}
                        />
                        <Text> Search by Title</Text>
                    </View>
                </View>
            )}

            <ScrollView contentContainerStyle={styles.scrollViewContent}>
                {changeListView ? (
                    <View style={styles.exponateGrid}>
                        {filteredExponate.length > 0 ? (
                            filteredExponate.map((exponat, index) => (
                                <View key={index} style={styles.exponateBox}>
                                    <TouchableOpacity>
                                        <View style={{ margin: 10 }}>
                                            <Image style={styles.images} source={listic} />  {/* Beispielbild */}
                                        </View>
                                        <View style={{ backgroundColor: "lightgrey", height: 2, width: "100%", marginTop: 4, marginBottom: 4 }}></View>
                                        <View>
                                            <Text style={styles.exponateTitle}>{exponat.title}</Text>
                                            <Text style={styles.exponateDetails}>{exponat.details}</Text>
                                        </View>
                                    </TouchableOpacity>
                                </View>
                            ))
                        ) : (
                            <Text>No Exponate found</Text>
                        )}
                    </View>
                ) : (
                    <View style={styles.simpleList}>
                        {filteredExponate.length > 0 ? (
                            filteredExponate.map((exponat, index) => (
                                <View style={{ width: 1300, flexDirection: "row" }} key={index}>
                                    <View style={styles.simpleListItem}>
                                        <View style={{ flexDirection: "row", width: 1260 }}>
                                            <Text style={{ fontWeight: "bold" }}>{exponat.title}</Text>
                                            <Text>... / TODO add imp params</Text>
                                        </View>
                                        <View>
                                            <TouchableOpacity>
                                                <Image style={{ width: 25, height: 25 }} source={arrow} />
                                            </TouchableOpacity>
                                        </View>
                                    </View>
                                </View>
                            ))
                        ) : (
                            <Text>No Exponate found</Text>
                        )}
                    </View>
                )}
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: '#fff',
        alignItems: 'flex-start',
    },
    welcomeText: {
        color: 'rgba(240,175,77,1)',
        fontWeight: "bold",
        fontSize: 50,
        marginBottom: 20,
    },
    searchContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 20,
        marginLeft: 635,
        width: '30%',
    },
    searchBar: {
        flex: 1,
        padding: 10,
    },
    filterButton: {
        padding: 10,
        borderRadius: 5,
    },
    activeButton: {
        backgroundColor: 'rgba(240,175,77,1)',
    },
    dropdown: {
        position: 'absolute',
        top: 70,
        left: 964,
        right: 20,
        backgroundColor: 'white',
        borderRadius: 5,
        padding: 10,
        elevation: 50,
        width: 400,
        zIndex: 10,
        borderWidth: 1,
        borderColor: 'lightgray',
    },
    checkBoxItem: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 10,
    },
    scrollViewContent: {
        alignItems: 'center',
    },
    exponateGrid: {
        flexDirection: 'row',
        flexWrap: 'wrap',
    },
    exponateBox: {
        width: '15%',
        borderWidth: 1,
        borderColor: 'lightgray',
        borderRadius: 5,
        padding: 2,
        marginBottom: 10,
        alignItems: 'center',
        justifyContent: 'center',
        height: 150,
        margin: 10,
    },
    exponateTitle: {
        fontSize: 14,
    },
    exponateDetails: {
        fontSize: 12,
        color: 'grey',
    },
    images: {
        width: 40,
        height: 40,
    },
    simpleList: {
        padding: 10,
        backgroundColor: '#f9f9f9',
        borderRadius: 5,
        width: '100%',
    },
    simpleListItem: {
        flexDirection: "row",
        fontSize: 16,
        paddingVertical: 5,
        borderBottomWidth: 1,
        borderBottomColor: '#ddd',
    },
});

export default Exponate;
