import './App.css';
import React, { useState } from 'react'; // importiere useState
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from "./Components/Home";
import Gallery from "./Components/Gallery"; // Ensure this file exists
import Settings from "./Components/Settings"; // Ensure this file exists
import Exponate from "./Components/Exponate";
import Auction from "./Components/Auction";
import Persons from "./Components/Persons";
import Graphs from "./Components/Graphs";
import LeftBar from "./Components/AlwaysActive/LeftBar";
import TopBar from './Components/AlwaysActive/TopBar';
import Search from './Components/Search';
import Login from "./Components/Login";
import { StyleSheet, View } from 'react-native';

function App() {
    const [fLogin, setLogin] = useState(true); // Setze den initialen Login-Status

    return (
        <Router>
            <div className="Kinsky-Frontend">
                {fLogin ? (
                    <View style={stylesContainers.containerRow}>
                        <LeftBar />
                        <View style={stylesContainers.containerColumn}>
                            <TopBar />
                            <View style={stylesContainers.content}>
                                <Routes>
                                    <Route path="/" element={<Home />} />
                                    <Route path="/gallery" element={<Gallery />} />
                                    <Route path="/settings" element={<Settings />} />
                                    <Route path="/exponate" element={<Exponate />} />
                                    <Route path="/persons" element={<Persons />} />
                                    <Route path="/search" element={<Search />} />
                                    <Route path="/auction" element={<Auction />} />
                                    <Route path="/graphs" element={<Graphs />} />
                                </Routes>
                            </View>
                        </View>
                    </View>
                ) : (
                    // Zeige nur die Login-Seite an, wenn fLogin false ist
                    <Login setLogin={setLogin} />
                )}
            </div>
        </Router>
    );
}

const stylesContainers = StyleSheet.create({
    containerRow: {
        flexDirection: "row",
        height: '100vh',
    },
    containerColumn: {
        flexDirection: "column",
        flex: 1,
    },
    content: {
        flex: 1,
        padding: 10,
    },
});

export default App;
