import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, Picker, ActivityIndicator } from "react-native";
import {
  LineChart,
  BarChart,
  PieChart,
  ProgressChart,
  StackedBarChart,
} from "react-native-chart-kit";
import { Dimensions } from "react-native";

const screenWidth = Dimensions.get("window").width;

const Graphs = () => {
  const [data, setData] = useState([]);
  const [chartType, setChartType] = useState("line");
  const [metric, setMetric] = useState("sales");
  const [view, setView] = useState("date");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch the data
    async function fetchStatistics() {
      try {
        const stats = await ApiService.fetchEinbringer(); // API-Aufruf
        setData(stats);
      } catch (error) {
        console.error("Fehler beim Abrufen der Statistiken:", error);
      } finally {
        setLoading(false);
      }
    }

    fetchStatistics();
  }, []);

  const aggregateData = (data, key) => {
    const grouped = data.reduce((acc, item) => {
      const groupKey = item[key];
      acc[groupKey] = acc[groupKey] || { label: groupKey, value: 0 };
      acc[groupKey].value += item[metric] || 0; // Aggregiere nach der aktuellen Metrik
      return acc;
    }, {});

    return Object.values(grouped);
  };

  const renderLineChart = (aggregatedData) => {
    const labels = aggregatedData.map((d) => d.label);
    const values = aggregatedData.map((d) => d.value);

    return (
      <LineChart
        data={{
          labels: labels.length ? labels : ["Keine Daten"],
          datasets: [{ data: values.length ? values : [0] }],
        }}
        width={screenWidth - 32}
        height={220}
        yAxisSuffix={metric === "sales" ? " Stk" : " €"}
        chartConfig={chartConfig}
        style={styles.chartStyle}
      />
    );
  };

  const renderBarChart = (aggregatedData) => {
    const labels = aggregatedData.map((d) => d.label);
    const values = aggregatedData.map((d) => d.value);

    return (
      <BarChart
        data={{
          labels: labels,
          datasets: [{ data: values }],
        }}
        width={screenWidth - 32}
        height={220}
        chartConfig={chartConfig}
        style={styles.chartStyle}
      />
    );
  };

  const renderPieChart = (aggregatedData) => {
    const pieData = aggregatedData.map((d) => ({
      name: d.label,
      population: d.value,
      color: `#${Math.floor(Math.random() * 16777215).toString(16)}`,
      legendFontColor: "#7F7F7F",
      legendFontSize: 15,
    }));

    return (
      <PieChart
        data={pieData}
        width={screenWidth - 32}
        height={220}
        chartConfig={chartConfig}
        accessor="population"
        backgroundColor="transparent"
        paddingLeft="15"
        style={styles.chartStyle}
      />
    );
  };

  const renderChart = () => {
    if (loading) {
      return <ActivityIndicator size="large" color="#007bff" />;
    }

    if (!data.length) {
      return <Text style={styles.error}>Keine Daten verfügbar.</Text>;
    }

    const aggregatedData = aggregateData(data, view);

    switch (chartType) {
      case "line":
        return renderLineChart(aggregatedData);
      case "bar":
        return renderBarChart(aggregatedData);
      case "pie":
        return renderPieChart(aggregatedData);
      default:
        return <Text>Ungültiger Diagrammtyp</Text>;
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Auktionsstatistiken</Text>

      <View style={styles.controls}>
        <Picker
          selectedValue={chartType}
          style={styles.picker}
          onValueChange={(itemValue) => setChartType(itemValue)}
        >
          <Picker.Item label="Liniendiagramm" value="line" />
          <Picker.Item label="Balkendiagramm" value="bar" />
          <Picker.Item label="Tortendiagramm" value="pie" />
        </Picker>

        <Picker
          selectedValue={metric}
          style={styles.picker}
          onValueChange={(itemValue) => setMetric(itemValue)}
        >
          <Picker.Item label="Anzahl Verkäufe" value="sales" />
          <Picker.Item label="Umsatz" value="revenue" />
        </Picker>

        <Picker
          selectedValue={view}
          style={styles.picker}
          onValueChange={(itemValue) => setView(itemValue)}
        >
          <Picker.Item label="Nach Datum" value="date" />
          <Picker.Item label="Nach Kategorie" value="category" />
          <Picker.Item label="Nach Bundesland" value="state" />
        </Picker>
      </View>

      <View style={styles.chartContainer}>{renderChart()}</View>
    </View>
  );
};

const chartConfig = {
  backgroundColor: "#e26a00",
  backgroundGradientFrom: "#fb8c00",
  backgroundGradientTo: "#ffa726",
  decimalPlaces: 0,
  color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
  labelColor: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
};

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: "#f8f9fa",
    height: "100%",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
    textAlign: "center",
  },
  controls: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginBottom: 20,
  },
  picker: {
    flex: 1,
    marginHorizontal: 5,
    borderColor: "#ccc",
    borderWidth: 1,
    borderRadius: 5,
  },
  chartContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#fff",
    borderRadius: 10,
    padding: 10,
    shadowColor: "#000",
    shadowOpacity: 0.1,
    shadowOffset: { width: 0, height: 2 },
    shadowRadius: 4,
    elevation: 3,
  },
  chartStyle: {
    marginVertical: 8,
    borderRadius: 16,
  },
  error: {
    color: "red",
    textAlign: "center",
    fontSize: 18,
  },
});

export default Graphs;
