import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';

const Auctions = () => {
    return (
        <View style={styles.container}>
            {/* Header */}
            <View style={styles.header}>
                <Text style={styles.title}>Auktionen</Text>
            </View>

            {/* Button-Leiste und Informationen-Leiste */}
            <View style={styles.topBar}>
                <View style={styles.buttonsContainer}>
                    {["Allgemein", "Bieten", "Kaufaufträge", "Exponate", "Anforderungen"].map((button, index) => (
                        <TouchableOpacity key={index} style={styles.button}>
                            <Text style={styles.buttonText}>{button}</Text>
                        </TouchableOpacity>
                    ))}
                </View>
                <View style={styles.infoWrapper}>
                    <View style={styles.infoContainer}>
                        <Text style={styles.infoText}>Informationen</Text>
                    </View>
                    {/* Hauptbereich unter Informationen */}
                    <View style={styles.mainContent}></View>
                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        padding: 20,
    },
    header: {
        flexDirection: "row",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: 20,
    },
    title: {
        color: 'rgba(240,175,77,1)',
        fontWeight: 'bold',
        fontSize: 50,
    },
    topBar: {
        flexDirection: 'row',
        marginBottom: 0,
        flex: 1,
    },
    buttonsContainer: {
        flex: 3,
        backgroundColor: 'rgb(247,203,105)',
        flexDirection: 'row',
        alignItems: 'center',
        height: 50,
    },
    button: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    buttonText: {
        fontWeight: 'bold',
        color: 'black',
    },
    infoWrapper: {
        flex: 1,
        marginLeft: 10, // Abstand zur Buttons-Leiste
    },
    infoContainer: {
        backgroundColor: 'rgb(247,203,105)',
        justifyContent: 'center',
        alignItems: 'center',
        height: 50,
    },
    infoText: {
        fontWeight: 'bold',
        color: 'black',
        textAlign: 'center',
    },
    mainContent: {
        flex: 1,
        backgroundColor: 'rgb(253,241,219)',
        borderTopWidth: 1,
        borderTopColor: 'rgb(247,203,105)', // Abgrenzung
    },
});

export default Auctions;
