import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';
import { Platform } from 'react-native';
//import haus from "src/images/haus.png"
//import galerie from "src/images/galerie.png"
//import nutzer from "src/images/nutzer.png"
import balk1 from "./images/speisekarte.png";
import lup2 from "./images/suche.png";
import haus3 from "./images/haus.png";
import user4 from "./images/nutzer.png";
import bild5 from "./images/galerie.png";
import auk6 from "./images/richter.png";
import setting7 from "./images/die-einstellungen.png";


const LeftBar = () => {
    const [hoverStates, setHoverStates] = useState([false, false, false]);

    const handleHover = (index, isHovered) => {
        const updatedStates = [...hoverStates];
        updatedStates[index] = isHovered;
        setHoverStates(updatedStates);
    };

    const buttons = [balk1, lup2, haus3,user4,bild5,auk6];

    //<img src={setting} alt="setting"/>

    // 1 = 3balken ; 2 = lupe  ; 3 = haus ; 4 = menschen; 5 = bilder; 6 = hammer; 7 = settings;


    return (
        <View style={styles.container}>
            <View>
            {buttons.map((buttonLabel, index) => (
                <TouchableOpacity
                    key={index}
                    style={[
                        styles.buttons,
                        hoverStates[index] && styles.hoveredButton,
                    ]}
                    onMouseEnter={() => handleHover(index, true)}
                    onMouseLeave={() => handleHover(index, false)}
                >
                    <img 
                    style={styles.images}
                    src={buttonLabel} />
                </TouchableOpacity>
            ))}
            </View>
            <View style={styles.setdownview}>
            <TouchableOpacity
                style={styles.buttons}
                onMouseEnter={() => handleHover(7, true)}
                onMouseLeave={() => handleHover(7, false)}
            >
                 <img 
                    style={styles.images}
                    src={setting7} />
            </TouchableOpacity>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flexDirection: 'column',
        justifyContent: 'top',
        alignItems: 'center',
        width: 60,
        backgroundColor: 'rgba(250,228,181,1)',
        height: 900,
    },
    buttons: {
        padding: 20,
        marginTop: 10,
        backgroundColor: 'transparent', // Default background
    },
    hoveredButton: {
        backgroundColor: 'rgba(246,203,108,1)', // Hover effect background
    },
    images: {
        width: 20,
        height: 20,
    },
    setdownview:{
        marginTop:30,
    }
});

export default LeftBar;
