import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';
import { Platform } from 'react-native';
//import haus from "src/images/haus.png"
//import galerie from "src/images/galerie.png"
//import nutzer from "src/images/nutzer.png"
import setting from "./images/die-einstellungen.png";


const LeftBar = () => {
    const [hoverStates, setHoverStates] = useState([false, false, false]);

    const handleHover = (index, isHovered) => {
        const updatedStates = [...hoverStates];
        updatedStates[index] = isHovered;
        setHoverStates(updatedStates);
    };

    const buttons = ['1', '2', '3',"4","5","6"];

    return (
        <View style={styles.container}>
            <View>
            <img src={setting} alt="setting"/>
            {buttons.map((buttonLabel, index) => (
                <TouchableOpacity
                    key={index}
                    style={[
                        styles.buttons,
                        hoverStates[index] && styles.hoveredButton,
                    ]}
                    onMouseEnter={() => handleHover(index, true)}
                    onMouseLeave={() => handleHover(index, false)}
                >
                    <Text>{buttonLabel}</Text>
                </TouchableOpacity>
            ))}
            </View>
            <View>
            <TouchableOpacity
                style={styles.buttons}
                onMouseEnter={() => handleHover(7, true)}
                onMouseLeave={() => handleHover(7, false)}
            >7</TouchableOpacity>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flexDirection: 'column',
        justifyContent: 'top',
        alignItems: 'center',
        width: 60,
        backgroundColor: 'rgba(250,228,181,1)',
        height: 900,
    },
    buttons: {

        padding: 20,
        marginTop: 10,
        backgroundColor: 'transparent', // Default background
    },
    hoveredButton: {
        backgroundColor: 'rgba(246,203,108,1)', // Hover effect background
    },
});

export default LeftBar;
