import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { View, Text, StyleSheet, TouchableOpacity, Image, TextInput, Switch, Picker } from 'react-native';
import { personList } from '../../Application/Classes/testDataGenerator';

const PersonDetail = () => {
    const { id } = useParams();
    const person = personList.find(p => p.personenNr.toString() === id);

    const [imageUri, setImageUri] = useState('https://via.placeholder.com/250');
    const [selectedTab, setSelectedTab] = useState('Allgemein'); // Initialer Tab

    const handleImageUpload = () => {
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = 'image/*';
        input.onchange = (e) => {
            const file = e.target.files[0];
            if (file) {
                const reader = new FileReader();
                reader.onload = () => setImageUri(reader.result);
                reader.readAsDataURL(file);
            }
        };
        input.click();
    };

    const renderField = (field) => {
        if (field.type === 'text') {
            return (
                <TextInput
                    style={styles.input}
                    placeholder={field.value?.toString() || "Nicht angegeben"}
                />
            );
        } else if (field.type === 'boolean') {
            return (
                <Switch
                    value={field.value || false}
                    onValueChange={(newValue) => console.log(`Changed ${field.label} to ${newValue}`)}
                />
            );
        } else if (field.type === 'enum') {
            return (
                <Picker
                    selectedValue={field.value || ""}
                    style={styles.input}
                    onValueChange={(newValue) => console.log(`Selected ${field.label}: ${newValue}`)}
                >
                    {field.options.map((option, index) => (
                        <Picker.Item key={index} label={option} value={option} />
                    ))}
                </Picker>
            );
        }
        return null;
    };

    const renderTabContent = (fields) => (
        <View style={styles.contentColumns}>
            <View style={styles.leftColumn}>
                {fields.slice(0, Math.ceil(fields.length / 2)).map((field, index) => (
                    <View key={index} style={styles.row}>
                        <Text style={styles.label}>{field.label}:</Text>
                        {renderField(field)}
                    </View>
                ))}
            </View>
            <View style={styles.rightColumn}>
                {fields.slice(Math.ceil(fields.length / 2)).map((field, index) => (
                    <View key={index} style={styles.row}>
                        <Text style={styles.label}>{field.label}:</Text>
                        {renderField(field)}
                    </View>
                ))}
            </View>
        </View>
    );

    const renderContent = () => {
        switch (selectedTab) {
            case 'Allgemein':
                return renderTabContent([
                    { label: "Personen-Nummer", value: person.personenNr, type: 'text' },
                    { label: "Betreuer", value: person.betreuer, type: 'text' },
                    { label: "Korrespondenz", value: person.korrespondenz, type: 'text' },
                    { label: "Sprache", value: person.sprache, type: 'enum', options: ['Deutsch', 'English', 'Zweisprachig'] },
                    { label: "Anrede", value: person.anrede, type: 'enum', options: ['Frau', 'Herr', 'Divers'] },
                    { label: "Vorname", value: person.vorname, type: 'text' },
                    { label: "Nachname", value: person.nachname, type: 'text' },
                ]);
            case 'Kontakte':
                return renderTabContent([
                    { label: "Telefon", value: person.telefon, type: 'text' },
                    { label: "E-Mail", value: person.email, type: 'text' },
                    { label: "Adresse 1", value: `${person.adressen?.[0]?.strasse}, ${person.adressen?.[0]?.plz} ${person.adressen?.[0]?.ort}`, type: 'text' },
                    { label: "Adresse 2", value: `${person.adressen?.[1]?.strasse}, ${person.adressen?.[1]?.plz} ${person.adressen?.[1]?.ort}`, type: 'text' },
                ]);
            case 'Zuordnung':
                return renderTabContent([
                    { label: "Betreuer", value: person.betreuer?.vorname || "Kein Betreuer", type: 'text' },
                    { label: "Zugehörigkeit", value: person.zugehoerigkeit || "Keine Zugehörigkeit", type: 'text' },
                ]);
            case 'Einbringer':
                return renderTabContent([
                    { label: "Einbringer Name", value: person.einbringer?.vorname || "Kein Einbringer", type: 'text' },
                    { label: "Grad", value: person.einbringer?.grad || "Nicht angegeben", type: 'enum', options: ['T', 'G', 'M', 'K', 'E'] },
                ]);
            case 'Künstler':
                return renderTabContent([
                    { label: "Künstler Name", value: person.kuenstler?.vorname || "Kein Künstler", type: 'text' },
                    { label: "Pseudonym", value: person.kuenstler?.pseudonym1 || "Kein Pseudonym", type: 'text' },
                ]);
            case 'Exponate':
                return renderTabContent(
                    person.exponate?.map((exponat, index) => ({
                        label: `Exponat ${index + 1}`,
                        value: exponat.titel,
                        type: 'text',
                    })) || []
                );
            case 'Verrechnung':
                return renderTabContent([
                    { label: "UID", value: person.uid || "Keine UID", type: 'text' },
                    { label: "Bank", value: person.bank || "Keine Bankinformationen", type: 'text' },
                    { label: "IBAN", value: person.iban || "Keine IBAN", type: 'text' },
                ]);
            default:
                return <Text style={styles.tabContent}>Kein Inhalt ausgewählt.</Text>;
        }
    };

    if (!person) {
        return <Text style={styles.error}>Person nicht gefunden!</Text>;
    }

    return (
        <View style={styles.container}>
            <View style={styles.header}>
                <View style={styles.nameContainer}>
                    <Text style={styles.welcomeText}>{person.vorname}</Text>
                    <Text style={styles.welcomeText}>{person.nachname}</Text>
                </View>
                <TouchableOpacity onPress={handleImageUpload} style={styles.imageContainer}>
                    <Image source={{ uri: imageUri }} style={styles.image} />
                    <Text style={styles.imageHint}>Bild hinzufügen</Text>
                </TouchableOpacity>
            </View>
            <View style={styles.optionsBar}>
                {["Allgemein", "Kontakte", "Zuordnung", "Einbringer", "Künstler", "Exponate", "Verrechnung", "USW"].map((option, index) => (
                    <TouchableOpacity
                        key={index}
                        style={[
                            styles.optionButton,
                            selectedTab === option && styles.activeOptionButton,
                        ]}
                        onPress={() => setSelectedTab(option)}
                    >
                        <Text
                            style={[
                                styles.optionText,
                                selectedTab === option && styles.activeOptionText,
                            ]}
                        >
                            {option}
                        </Text>
                    </TouchableOpacity>
                ))}
            </View>
            <View style={styles.content}>{renderContent()}</View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20, backgroundColor: '#fff', height: '100vh' },
    error: { color: 'red', fontSize: 20, textAlign: 'center' },
    header: { flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20, height: 300 },
    nameContainer: { justifyContent: 'center', flex: 1 },
    welcomeText: { color: 'rgba(240,175,77,1)', fontWeight: "bold", fontSize: 40, marginBottom: 5 },
    imageContainer: { alignItems: 'flex-end', justifyContent: 'center', flex: 1 },
    image: { width: 250, height: 250, borderWidth: 2, borderColor: '#ddd', backgroundColor: '#f0f0f0' },
    imageHint: { marginTop: 5, fontSize: 12, color: 'gray' },
    optionsBar: { flexDirection: 'row', justifyContent: 'space-around', backgroundColor: 'rgb(247,203,105)', paddingVertical: 8, marginBottom: 15 },
    optionButton: { padding: 5 },
    activeOptionButton: { backgroundColor: 'rgb(240,175,77)', borderRadius: 5 },
    optionText: { color: 'black', fontWeight: 'bold' },
    activeOptionText: { color: 'white' },
    content: { backgroundColor: '#fff', flex: 1, padding: 10 },
    contentColumns: { flexDirection: 'row', justifyContent: 'space-between' },
    leftColumn: { width: '45%' },
    rightColumn: { width: '45%' },
    row: { flexDirection: 'row', alignItems: 'center', marginBottom: 10 },
    label: { fontSize: 14, fontWeight: 'bold', width: '40%' },
    input: { borderWidth: 1, borderColor: '#ccc', borderRadius: 5, padding: 5, width: '58%' },
});

export default PersonDetail;
