import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { View, Text, StyleSheet, ActivityIndicator } from 'react-native';
import { fetchAllPersons } from '../Application/personService';

const PersonDetail = () => {
    const { id } = useParams(); // ID aus der URL
    const [person, setPerson] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const loadPerson = async () => {
            try {
                const allPersons = await fetchAllPersons();
                const foundPerson = allPersons.find((p) => p.personenNr.toString() === id); // Finde die Person anhand der ID
                if (!foundPerson) {
                    setError("Person nicht gefunden.");
                } else {
                    setPerson(foundPerson);
                }
            } catch (err) {
                console.error("Fehler beim Laden der Personendetails:", err);
                setError("Daten konnten nicht geladen werden.");
            } finally {
                setLoading(false);
            }
        };

        loadPerson();
    }, [id]);

    if (loading) {
        return (
            <View style={styles.loader}>
                <ActivityIndicator size="large" color="#f0af4d" />
                <Text>Daten werden geladen...</Text>
            </View>
        );
    }

    if (error) {
        return (
            <View style={styles.errorContainer}>
                <Text style={styles.errorText}>{error}</Text>
            </View>
        );
    }

    if (!person) {
        return (
            <View style={styles.errorContainer}>
                <Text style={styles.errorText}>Person nicht gefunden!</Text>
            </View>
        );
    }

    return (
        <View style={styles.container}>
            <Text style={styles.header}>{person.vorname} {person.nachname}</Text>
            <View style={styles.detailContainer}>
                <Text style={styles.detailLabel}>Typ:</Text>
                <Text style={styles.detailValue}>{person.typ}</Text>
            </View>
            <View style={styles.detailContainer}>
                <Text style={styles.detailLabel}>E-Mail:</Text>
                <Text style={styles.detailValue}>{person.email || "Keine E-Mail angegeben"}</Text>
            </View>
            <View style={styles.detailContainer}>
                <Text style={styles.detailLabel}>Telefon:</Text>
                <Text style={styles.detailValue}>{person.telefon || "Keine Telefonnummer angegeben"}</Text>
            </View>
            <View style={styles.detailContainer}>
                <Text style={styles.detailLabel}>Adresse:</Text>
                <Text style={styles.detailValue}>{person.adresse || "Keine Adresse angegeben"}</Text>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20, backgroundColor: '#fff', flex: 1 },
    loader: { flex: 1, justifyContent: 'center', alignItems: 'center' },
    errorContainer: { flex: 1, justifyContent: 'center', alignItems: 'center' },
    errorText: { color: 'red', fontSize: 16 },
    header: { fontSize: 24, fontWeight: 'bold', marginBottom: 20 },
    detailContainer: { flexDirection: 'row', marginBottom: 10 },
    detailLabel: { fontWeight: 'bold', marginRight: 10 },
    detailValue: { color: 'gray' },
});

export default PersonDetail;
