import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Image } from 'react-native';
import { useNavigate } from 'react-router-dom'; // Use useNavigate instead of useHistory
import balk1 from "./images/speisekarte.png";
import lup2 from "./images/suche.png";
import haus3 from "./images/haus.png";
import user4 from "./images/nutzer.png";
import bild5 from "./images/galerie.png";
import auk6 from "./images/richter.png";
import gra7 from "./images/gr.png";
import setting8 from "./images/die-einstellungen.png";

const LeftBar = () => {
    const [hoveredIndex, setHoveredIndex] = useState(null);
    const navigate = useNavigate(); 

    const buttons = [
        { image: balk1, route: '/' },
        { image: lup2, route: '/search' },
        { image: haus3, route: '/exponate' },
        { image: user4, route: '/persons' }, 
        { image: bild5, route: '/gallery' },
        { image: auk6, route: '/auction' },
        { image: gra7, route: '/graphs' },

    ];

    const handleMouseEnter = (index) => {
        setHoveredIndex(index);
    };

    const handleMouseLeave = () => {
        setHoveredIndex(null);
    };

    const handleNavigation = (route) => {
        navigate(route); // Navigate to the corresponding route
    };

    return (
        <View style={styles.container}>
            <View>
                {buttons.map((button, index) => (
                    <TouchableOpacity
                        key={index}
                        style={[
                            styles.buttons,
                            hoveredIndex === index && styles.hoveredButton,
                        ]}
                        onMouseEnter={() => handleMouseEnter(index)}
                        onMouseLeave={handleMouseLeave}
                        onPress={() => handleNavigation(button.route)}
                    >
                        <Image
                            style={styles.images}
                            source={button.image}
                        />
                    </TouchableOpacity>
                ))}
            </View>
            <View style={styles.setdownview}>
                <TouchableOpacity
                    style={[
                        styles.buttons,
                        hoveredIndex === buttons.length && styles.hoveredButton, // Changed to buttons.length
                    ]}
                    onMouseEnter={() => handleMouseEnter(buttons.length)}
                    onMouseLeave={handleMouseLeave}
                    onPress={() => handleNavigation('/settings')}
                >
                    <Image
                        style={styles.images}
                        source={setting8}
                    />
                </TouchableOpacity>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'center',
        width: 77,
        backgroundColor: 'rgba(250,228,181,1)',
        height: 900,
    },
    buttons: {
        padding: 25,
        backgroundColor: 'transparent',
    },
    hoveredButton: {
        backgroundColor: 'rgba(246,203,108,1)',
    },
    images: {
        width: 25,
        height: 25,
    },
    setdownview: {
        marginTop: 280,
    },
});

export default LeftBar;
