import React, { useEffect, useState } from "react";
import { ScrollView, ActivityIndicator, useWindowDimensions } from "react-native";
import RenderHtml from "react-native-render-html";

const Graphs = () => {
    const { width } = useWindowDimensions();
    const [htmlContent, setHtmlContent] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch("/html/statistics.html")  // Stelle sicher, dass die Datei im "public/html/" Ordner liegt
            .then((response) => response.text())
            .then((html) => {
                setHtmlContent(html);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Fehler beim Laden der HTML-Datei:", error);
                setLoading(false);
            });
    }, []);

    // ðŸš€ Funktion, um externe Skripte auszufÃ¼hren
    const executeScripts = () => {
        const scriptUrls = [
            "https://d3js.org/d3.v7.min.js",
            "https://cdnjs.cloudflare.com/ajax/libs/html2canvas/1.4.1/html2canvas.min.js",
            "https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.4.0/jspdf.umd.min.js"
        ];

        scriptUrls.forEach((src) => {
            const script = document.createElement("script");
            script.src = src;
            script.async = true;
            document.body.appendChild(script);
        });

        console.log("Alle externen Skripte wurden geladen!");
    };

    useEffect(() => {
        if (htmlContent) {
            executeScripts();
        }
    }, [htmlContent]);

    if (loading) {
        return <ActivityIndicator size="large" color="#0000ff" />;
    }

    return (
        <ScrollView style={{ flex: 1, padding: 10 }}>
            {htmlContent && (
                <RenderHtml
                    contentWidth={width}
                    source={{ html: htmlContent }}
                />
            )}
        </ScrollView>
    );
};

export default Graphs;
