import React, { useState } from 'react';
import { View, Text, StyleSheet, Switch, TextInput, TouchableOpacity } from 'react-native';

const Settings = () => {
    const [darkMode, setDarkMode] = useState(false);
    const [username, setUsername] = useState('JohnDoe');
    const [email, setEmail] = useState('john.doe@example.com');
    const [notificationsEnabled, setNotificationsEnabled] = useState(true);

    const toggleDarkMode = () => setDarkMode(!darkMode);

    return (
        <View style={[styles.container, darkMode ? styles.darkContainer : styles.lightContainer]}>
            <Text style={[styles.title, darkMode ? styles.darkText : styles.lightText]}>Einstellungen</Text>

            {/* Username */}
            <View style={styles.settingRow}>
                <Text style={[styles.label, darkMode ? styles.darkText : styles.lightText]}>Benutzername:</Text>
                <TextInput
                    style={[styles.input, darkMode ? styles.darkInput : styles.lightInput]}
                    value={username}
                    onChangeText={setUsername}
                    placeholder="Benutzername eingeben"
                />
            </View>

            {/* Email */}
            <View style={styles.settingRow}>
                <Text style={[styles.label, darkMode ? styles.darkText : styles.lightText]}>E-Mail:</Text>
                <TextInput
                    style={[styles.input, darkMode ? styles.darkInput : styles.lightInput]}
                    value={email}
                    onChangeText={setEmail}
                    placeholder="E-Mail eingeben"
                />
            </View>

            {/* Notifications */}
            <View style={styles.settingRow}>
                <Text style={[styles.label, darkMode ? styles.darkText : styles.lightText]}>
                    Benachrichtigungen aktivieren:
                </Text>
                <Switch
                    value={notificationsEnabled}
                    onValueChange={setNotificationsEnabled}
                    thumbColor={darkMode ? "#f5dd4b" : "#f4f3f4"}
                    trackColor={{ false: "#767577", true: "#81b0ff" }}
                />
            </View>

            {/* Dark Mode */}
            <View style={styles.settingRow}>
                <Text style={[styles.label, darkMode ? styles.darkText : styles.lightText]}>Dark Mode:</Text>
                <Switch
                    value={darkMode}
                    onValueChange={toggleDarkMode}
                    thumbColor={darkMode ? "#f5dd4b" : "#f4f3f4"}
                    trackColor={{ false: "#767577", true: "#81b0ff" }}
                />
            </View>

            {/* Save Button */}
            <TouchableOpacity style={[styles.saveButton, darkMode ? styles.darkButton : styles.lightButton]}>
                <Text style={darkMode ? styles.darkButtonText : styles.lightButtonText}>Änderungen speichern</Text>
            </TouchableOpacity>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
    },
    lightContainer: {
        backgroundColor: '#fff',
    },
    darkContainer: {
        backgroundColor: '#121212',
    },
    title: {
        fontSize: 32,
        fontWeight: 'bold',
        marginBottom: 20,
    },
    lightText: {
        color: '#000',
    },
    darkText: {
        color: '#fff',
    },
    settingRow: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 15,
    },
    label: {
        fontSize: 16,
        fontWeight: 'bold',
    },
    input: {
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 5,
        padding: 8,
        width: '60%',
    },
    lightInput: {
        backgroundColor: '#fff',
        color: '#000',
    },
    darkInput: {
        backgroundColor: '#333',
        color: '#fff',
    },
    saveButton: {
        padding: 15,
        alignItems: 'center',
        borderRadius: 5,
        marginTop: 20,
    },
    lightButton: {
        backgroundColor: '#007BFF',
    },
    darkButton: {
        backgroundColor: '#1E90FF',
    },
    lightButtonText: {
        color: '#fff',
    },
    darkButtonText: {
        color: '#fff',
    },
});

export default Settings;
