import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { View, Text, StyleSheet, TouchableOpacity, Image, TextInput, Switch, Picker } from 'react-native';
import { fetchAllPersons } from '../../Application/personService';

const PersonDetail = () => {
    const { id } = useParams(); // ID aus der URL
    const [person, setPerson] = useState(null);
    const [imageUri, setImageUri] = useState('https://via.placeholder.com/250');
    const [selectedTab, setSelectedTab] = useState('Allgemein');
    const [error, setError] = useState(null);

    useEffect(() => {
        const loadPerson = async () => {
            console.log("PersonDetail: Start loading person with ID:", id);
            try {
                const allPersons = await fetchAllPersons();
                console.log("PersonDetail: Loaded persons list:", allPersons);
                const foundPerson = allPersons.find((p) => p.personenNr.toString() === id);
                if (!foundPerson) {
                    console.error("PersonDetail: Person not found with ID:", id);
                    setError("Person nicht gefunden!");
                } else {
                    console.log("PersonDetail: Found person:", foundPerson);
                    setPerson(foundPerson);
                }
            } catch (err) {
                console.error("PersonDetail: Error loading persons:", err);
                setError("Daten konnten nicht geladen werden.");
            }
        };

        loadPerson();
    }, [id]);

    if (error) {
        return (
            <View style={styles.container}>
                <Text style={styles.error}>Fehler: {error}</Text>
            </View>
        );
    }

    if (!person) {
        return (
            <View style={styles.container}>
                <Text style={styles.error}>Person nicht gefunden!</Text>
            </View>
        );
    }

    return (
        <View style={styles.container}>
            {/* RENDERING LAYOUT */}
        </View>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20, backgroundColor: '#fff', height: '100vh' },
    error: { color: 'red', fontSize: 20, textAlign: 'center' },
});

export default PersonDetail;
