/**
 * ApiService.js
 * Zentrale Klasse zum Importieren aller Funktionen und Modelle.
 */

// Importiere alle Modelle und Enums
import Adresse from './Adresse.js';
import Person from './Person.js';
import Betreuer from './Betreuer.js';
import Einbringer from './Einbringer.js';
import Exponat from './Exponat.js';
import Kaeufer from './Kaeufer.js';
import Kuenstler from './Kuenstler.js';
import Vermittler from './Vermittler.js';
import Dokument from './Dokument.js';
import { Sprache, Anrede, Land, Waehrung, Steuertyp, Grad } from './Enums.js';

// Importiere alle API-Funktionen aus fetchAndRead.js
import { fetchData, postData, updateData, deleteData } from './fetchAndRead.js';

// Definiere die API-Endpunkte
const ENDPOINTS = {
    PERSONS: '/persons',
    BETREUER: '/betreuer',
    EINBRINGER: '/einbringer',
    EXPONATE: '/exponate',
    KAEUFER: '/kaeufer',
    KUENSTLER: '/kuenstler',
    VERMITTLER: '/vermittler',
    DOKUMENTE: '/dokumente',
};

/**
 * ApiService
 * Bietet zentrale Funktionen zum Arbeiten mit dem Backend.
 */
exports.module = class ApiService {
    // Fetch-Funktionen für verschiedene Ressourcen
    static async fetchPersons() {
        return fetchData(ENDPOINTS.PERSONS);
    }

    static async fetchBetreuer() {
        return fetchData(ENDPOINTS.BETREUER);
    }

    static async fetchEinbringer() {
        return fetchData(ENDPOINTS.EINBRINGER);
    }

    static async fetchExponate() {
        return fetchData(ENDPOINTS.EXPONATE);
    }

    static async fetchKaeufer() {
        return fetchData(ENDPOINTS.KAEUFER);
    }

    static async fetchKuenstler() {
        return fetchData(ENDPOINTS.KUENSTLER);
    }

    static async fetchVermittler() {
        return fetchData(ENDPOINTS.VERMITTLER);
    }

    static async fetchDokumente() {
        return fetchData(ENDPOINTS.DOKUMENTE);
    }

    // Testfunktion: Daten abrufen und in der Konsole ausgeben
    static async runTest() {
        console.log("Running API Service Test...");

        try {
            // Test: Abruf von Personen
            const persons = await this.fetchPersons();
            console.log("Personen-Daten:", persons);

            // Test: Abruf von Exponaten
            const exponate = await this.fetchExponate();
            console.log("Exponate-Daten:", exponate);

            // Test: Abruf von Künstlern
            const kuenstler = await this.fetchKuenstler();
            console.log("Künstler-Daten:", kuenstler);
        } catch (error) {
            console.error("Fehler beim Abrufen von Daten:", error);
        }
    }
}

// Exportiere ApiService und alle Enums und Modelle
export default ApiService;
export {
    Adresse,
    Person,
    Betreuer,
    Einbringer,
    Exponat,
    Kaeufer,
    Kuenstler,
    Vermittler,
    Dokument,
    Sprache,
    Anrede,
    Land,
    Waehrung,
    Steuertyp,
    Grad,
};

// Starte den Test automatisch beim Laden
ApiService.runTest();
