import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, Picker, ActivityIndicator } from "react-native";
import { LineChart, BarChart, PieChart } from "react-native-chart-kit"; // Weitere Diagrammtypen
import fetchChartData from "../Services/ChartService";

const Graphs = () => {
    const [chartData, setChartData] = useState([]);
    const [chartType, setChartType] = useState("line");
    const [category, setCategory] = useState("vermittler");
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch the chart data
        async function loadChartData() {
            try {
                setLoading(true);
                const data = await fetchChartData();
                setChartData(data);
            } catch (error) {
                console.error("Fehler beim Laden der Diagrammdaten:", error);
            } finally {
                setLoading(false);
            }
        }

        loadChartData();
    }, []);

    const renderChart = () => {
        if (loading) {
            return <ActivityIndicator size="large" color="#007bff" />;
        }

        const data = chartData[category] || [];
        const labels = data.map((item) => item.label);
        const values = data.map((item) => item.value);

        switch (chartType) {
            case "line":
                return (
                    <LineChart
                        data={{
                            labels: labels,
                            datasets: [{ data: values }],
                        }}
                        width={350}
                        height={220}
                        chartConfig={chartConfig}
                        style={styles.chart}
                    />
                );
            case "bar":
                return (
                    <BarChart
                        data={{
                            labels: labels,
                            datasets: [{ data: values }],
                        }}
                        width={350}
                        height={220}
                        chartConfig={chartConfig}
                        style={styles.chart}
                    />
                );
            case "pie":
                return (
                    <PieChart
                        data={data.map((item) => ({
                            name: item.label,
                            value: item.value,
                            color: `#${Math.floor(Math.random() * 16777215).toString(16)}`,
                            legendFontColor: "#7F7F7F",
                            legendFontSize: 15,
                        }))}
                        width={350}
                        height={220}
                        accessor="value"
                        backgroundColor="transparent"
                        style={styles.chart}
                    />
                );
            default:
                return <Text>Ung√ºltiger Diagrammtyp</Text>;
        }
    };

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Diagramme</Text>

            <View style={styles.controls}>
                <Picker
                    selectedValue={chartType}
                    style={styles.picker}
                    onValueChange={(itemValue) => setChartType(itemValue)}
                >
                    <Picker.Item label="Liniendiagramm" value="line" />
                    <Picker.Item label="Balkendiagramm" value="bar" />
                    <Picker.Item label="Kreisdiagramm" value="pie" />
                </Picker>

                <Picker
                    selectedValue={category}
                    style={styles.picker}
                    onValueChange={(itemValue) => setCategory(itemValue)}
                >
                    <Picker.Item label="Vermittler" value="vermittler" />
                    <Picker.Item label="Einbringer" value="einbringer" />
                    <Picker.Item label="Betreuer" value="betreuer" />
                    <Picker.Item label="Exponate" value="exponate" />
                    <Picker.Item label="Auktionen" value="auktionen" />
                </Picker>
            </View>

            <View style={styles.chartContainer}>{renderChart()}</View>
        </View>
    );
};

const chartConfig = {
    backgroundGradientFrom: "#fb8c00",
    backgroundGradientTo: "#ffa726",
    color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
    labelColor: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
    decimalPlaces: 0,
};

const styles = StyleSheet.create({
    container: {
        padding: 20,
        backgroundColor: "#f8f9fa",
        height: "100%",
    },
    title: {
        fontSize: 24,
        fontWeight: "bold",
        marginBottom: 20,
        textAlign: "center",
    },
    controls: {
        flexDirection: "row",
        justifyContent: "space-between",
        marginBottom: 20,
    },
    picker: {
        flex: 1,
        marginHorizontal: 5,
        borderColor: "#ccc",
        borderWidth: 1,
        borderRadius: 5,
    },
    chartContainer: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: "#fff",
        borderRadius: 10,
        padding: 10,
        shadowColor: "#000",
        shadowOpacity: 0.1,
        shadowOffset: { width: 0, height: 2 },
        shadowRadius: 4,
        elevation: 3,
    },
    chart: {
        marginVertical: 8,
        borderRadius: 16,
    },
});

export default Graphs;
