import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { View, Text, StyleSheet, TouchableOpacity, Image, TextInput, Switch, Picker } from 'react-native';

const PersonDetail = ({ persons, darkMode }) => {
    const { id } = useParams(); // ID aus der URL
    const [person, setPerson] = useState(null);
    const [imageUri, setImageUri] = useState('https://via.placeholder.com/250');
    const [selectedTab, setSelectedTab] = useState('Allgemein'); // Initialer Tab
    const [error, setError] = useState(null);

    useEffect(() => {
        console.log("DETAILS: ", JSON.stringify(persons, null, 2));
        console.log("URL Parameter (id): ", id);

        if (id && persons) {
            const foundPerson = persons?.find((p) => p?.id?.toString() === id);
            console.log("Gefundene Person: ", foundPerson);

            if (!foundPerson) {
                setError(`Keine Person mit der ID ${id} gefunden.`);
            } else {
                setPerson(foundPerson);
            }
        } else {
            setError("Fehlerhafte Daten: URL-Parameter oder Personenliste fehlt.");
        }
    }, [id, persons]);

    const handleImageUpload = () => {
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = 'image/*';
        input.onchange = (e) => {
            const file = e.target.files[0];
            if (file) {
                const reader = new FileReader();
                reader.onload = () => setImageUri(reader.result);
                reader.readAsDataURL(file);
            }
        };
        input.click();
    };

    const renderField = (field) => {
        if (field.type === 'text') {
            return (
                <TextInput
                    style={[styles.input, darkMode ? styles.darkInput : styles.lightInput]}
                    value={field.value?.toString() || 'Nicht angegeben'}
                    placeholder={field.placeholder || 'Nicht angegeben'}
                    editable={false}
                />
            );
        } else if (field.type === 'boolean') {
            return (
                <Switch
                    value={field.value || false}
                    disabled={true}
                />
            );
        } else if (field.type === 'enum') {
            return (
                <Picker
                    selectedValue={field.value || ''}
                    style={[styles.input, darkMode ? styles.darkInput : styles.lightInput]}
                    enabled={false}
                >
                    {field.options.map((option, index) => (
                        <Picker.Item key={index} label={option} value={option} />
                    ))}
                </Picker>
            );
        }
        return null;
    };

    const renderTabContent = (fields) => (
        <View style={styles.contentColumns}>
            <View style={styles.leftColumn}>
                {fields.slice(0, Math.ceil(fields.length / 2)).map((field, index) => (
                    <View key={index} style={styles.row}>
                        <Text style={[styles.label, darkMode ? styles.darkText : styles.lightText]}>{field.label}:</Text>
                        {renderField(field)}
                    </View>
                ))}
            </View>
            <View style={styles.rightColumn}>
                {fields.slice(Math.ceil(fields.length / 2)).map((field, index) => (
                    <View key={index} style={styles.row}>
                        <Text style={[styles.label, darkMode ? styles.darkText : styles.lightText]}>{field.label}:</Text>
                        {renderField(field)}
                    </View>
                ))}
            </View>
        </View>
    );

    const renderContent = () => {
        if (!person) return null;

        return renderTabContent([
            { label: 'Personen-Nummer', value: person.id, type: 'text' },
            { label: 'Vorname', value: person.vorname, type: 'text' },
            { label: 'Nachname', value: person.nachname, type: 'text' },
            { label: 'E-Mail', value: person.email, type: 'text' },
            { label: 'Telefon', value: person.telefon, type: 'text' },
            { label: 'Adresse', value: person.adresse, type: 'text' },
            { label: 'Typ', value: person.typ, type: 'text' },
        ]);
    };

    if (error) {
        return (
            <View style={[styles.container, darkMode ? styles.darkBg : styles.lightBg]}>
                <Text style={[styles.error, darkMode ? styles.darkText : styles.lightText]}>{error}</Text>
            </View>
        );
    }

    return (
        <View style={[styles.container, darkMode ? styles.darkBg : styles.lightBg]}>
            <View style={styles.header}>
                <View style={styles.nameContainer}>
                    <Text style={[styles.welcomeText, darkMode ? styles.darkWelcomeText : styles.lightWelcomeText]}>
                        {person?.vorname || 'Unbekannt'}
                    </Text>
                    <Text style={[styles.welcomeText, darkMode ? styles.darkWelcomeText : styles.lightWelcomeText]}>
                        {person?.nachname || 'Unbekannt'}
                    </Text>
                </View>
                <TouchableOpacity onPress={handleImageUpload} style={styles.imageContainer}>
                    <Image source={{ uri: imageUri }} style={styles.image} />
                    <Text style={[styles.imageHint, darkMode ? styles.darkText : styles.lightText]}>
                        Bild hinzuf√ºgen
                    </Text>
                </TouchableOpacity>
            </View>
            <View style={[styles.optionsBar, darkMode ? styles.darkButtonBg : styles.lightButtonBg]}>
                {['Allgemein', 'Kontakte', 'Zuordnung', 'Einbringer', 'Verrechnung'].map((option, index) => (
                    <TouchableOpacity
                        key={index}
                        style={[
                            styles.optionButton,
                            selectedTab === option && (darkMode ? styles.darkActiveButton : styles.lightActiveButton),
                        ]}
                        onPress={() => setSelectedTab(option)}
                    >
                        <Text
                            style={[
                                styles.optionText,
                                selectedTab === option && (darkMode ? styles.darkActiveText : styles.lightActiveText),
                            ]}
                        >
                            {option}
                        </Text>
                    </TouchableOpacity>
                ))}
            </View>
            <View style={styles.content}>{renderContent()}</View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20, height: '100vh' },
    lightBg: { backgroundColor: '#fff' },
    darkBg: { backgroundColor: 'rgb(18, 18, 18)' },

    error: { fontSize: 20, textAlign: 'center' },
    darkText: { color: "#fff" },
    lightText: { color: "#000" },

    header: { flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20 },
    nameContainer: { justifyContent: 'center', flex: 1 },
    welcomeText: { fontWeight: 'bold', fontSize: 40, marginBottom: 5 },
    lightWelcomeText: { color: 'rgba(240,175,77,1)' },
    darkWelcomeText: { color: 'rgb(85, 129, 176)' },

    imageContainer: { alignItems: 'flex-end', flex: 1 },
    image: { width: 250, height: 250, borderWidth: 2 },
    imageHint: { marginTop: 5, fontSize: 12 },

    optionsBar: { flexDirection: 'row', justifyContent: 'space-around', paddingVertical: 8, marginBottom: 15 },
    lightButtonBg: { backgroundColor: 'rgb(247,203,105)' },
    darkButtonBg: { backgroundColor: 'rgb(29, 67, 158)' },

    optionButton: { padding: 5 },
    lightActiveButton: { backgroundColor: 'rgb(240,175,77)' },
    darkActiveButton: { backgroundColor: 'rgb(18, 44, 90)' },

    optionText: { fontWeight: 'bold' },
    lightActiveText: { color: 'white' },
    darkActiveText: { color: 'rgb(200, 230, 255)' },
});

export default PersonDetail;
