import { StyleSheet } from 'react-native';

const globalStyles = StyleSheet.create({
    // Containers
    container: {
        flex: 1,
        backgroundColor: '#fff',
        padding: 20,
    },
    row: {
        flexDirection: 'row',
        alignItems: 'center',
    },
    center: {
        justifyContent: 'center',
        alignItems: 'center',
    },
    borderBox: {
        borderWidth: 1,
        borderColor: 'lightgray',
        borderRadius: 5,
    },

    // Texts
    titleText: {
        color: 'rgba(240,175,77,1)',
        fontWeight: 'bold',
        fontSize: 50,
    },
    label: {
        fontSize: 14,
        fontWeight: 'bold',
    },
    listValueName: {
        fontWeight: 'bold',
        fontSize: 14,
    },
    buttonText: {
        fontWeight: 'bold',
        color: 'black',
    },

    // Inputs
    input: {
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 5,
        padding: 5,
    },
    textArea: {
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 5,
        textAlignVertical: 'top',
        padding: 10,
        fontSize: 14,
    },

    // Buttons
    button: {
        justifyContent: 'center',
        alignItems: 'center',
        padding: 5,
    },

    // Images
    imageSmall: {
        width: 20,
        height: 20,
    },
    imageLarge: {
        width: 250,
        height: 250,
        borderWidth: 2,
        borderColor: '#ddd',
        backgroundColor: '#f0f0f0',
    },
});

export default globalStyles;
