import './App.css';
import React, { useState, useEffect } from 'react'; // Importiere useEffect
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from "./Components/Home";
import Gallery from "./Components/Gallery";
import Settings from "./Components/Settings";
import Exponate from "./Components/Exponate";
import Auction from "./Components/Auction";
import Persons from "./Components/Persons";
import Graphs from "./Components/Graphs";
import LeftBar from "./Components/AlwaysActive/LeftBar";
import TopBar from './Components/AlwaysActive/TopBar';
import Search from './Components/Search';
import Login from "./Components/Login";
import PersonDetail from './Components/FurtherInformation/PersonDetail';
import { StyleSheet, View } from 'react-native';
import { fetchAllPersons } from './Application/personService';

function App() {
    const [fLogin, setLogin] = useState(false); // Login-Status
    const [persons, setPersons] = useState([]); // Personenliste
    const [darkMode, setDarkMode] = useState(false);
    const [isEnglish, setEnglish] = useState(false);

    // Lade Personen beim Start
    useEffect(() => {
        const loadPersons = async () => {
            try {
                const data = await fetchAllPersons();
                setPersons(data);
            } catch (error) {
                console.error("Fehler beim Laden der Personen:", error);
            }
        };

        loadPersons();
    }, []);

    const toggleLogin = () => {
        setLogin(!fLogin);
    };

    return (
        <Router>
            <div className="Kinsky-Frontend">
                {fLogin ? (
                    <View style={stylesContainers.containerRow}>
                        <LeftBar />
                        <View style={stylesContainers.containerColumn}>
                            <TopBar />
                            <View style={stylesContainers.content}>
                                <Routes>
                                    <Route path="/" element={<Home />} />
                                    <Route path="/gallery" element={<Gallery />} />
                                    <Route path="/settings" element={<Settings />} />
                                    <Route path="/exponate" element={<Exponate />} />
                                    <Route path="/persons" element={<Persons persons={persons} />} />
                                    <Route path="/person/:id" element={<PersonDetail persons={persons} />} />
                                    <Route path="/search" element={<Search />} />
                                    <Route path="/auction" element={<Auction />} />
                                    <Route path="/graphs" element={<Graphs />} />
                                </Routes>
                            </View>
                        </View>
                    </View>
                ) : (
                    <Login setLogin={setLogin} />
                )}
            </div>
        </Router>
    );
}

const stylesContainers = StyleSheet.create({
    containerRow: { flexDirection: "row", height: '100vh' },
    containerColumn: { flexDirection: "column", flex: 1 },
    content: { flex: 1, padding: 10 },
});

export default App;
