import React from 'react';
import { Group } from '@visx/group';
import { Bar } from '@visx/shape';
import { scaleBand, scaleLinear } from '@visx/scale';
import { AxisBottom, AxisLeft } from '@visx/axis';
import { Tooltip, useTooltip } from '@visx/tooltip';
import { localPoint } from '@visx/event';
import { people } from './DataList';

// Datenverarbeitung
const ageDistribution = getCounts(people, 'age');

const width = 600;
const height = 400;
const margin = { top: 20, right: 20, bottom: 30, left: 40 };

// Utility-Funktion zum Z채hlen der Werte
function getCounts(data, key) {
  const counts = {};
  data.forEach(d => {
    if (d[key] !== undefined) { // Sicherstellen, dass der Schl체ssel vorhanden ist
      counts[d[key]] = (counts[d[key]] || 0) + 1;
    }
  });
  return counts;
}

const MyCharts2 = () => {
  const { tooltipData, tooltipLeft, tooltipTop, showTooltip, hideTooltip } = useTooltip();

  // Skalierungen
  const xScaleAge = scaleBand({
    domain: Object.keys(ageDistribution),
    range: [margin.left, width - margin.right],
    padding: 0.4,
  });
  const yScaleAge = scaleLinear({
    domain: [0, Math.max(...Object.values(ageDistribution))],
    range: [height - margin.bottom, margin.top],
  });

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ width, height }}>
        <h2>Altersverteilung</h2>
        <svg width={width} height={height}>
          <Group>
            {Object.keys(ageDistribution).map((age, index) => (
              <Bar
                key={index}
                x={xScaleAge(age)}
                y={yScaleAge(ageDistribution[age])}
                width={xScaleAge.bandwidth()}
                height={height - yScaleAge(ageDistribution[age]) - margin.bottom}
                fill="teal"
                onMouseEnter={(event) => {
                  const coords = localPoint(event) || { x: 0, y: 0 };
                  showTooltip({
                    tooltipData: ageDistribution[age],
                    tooltipTop: coords.y,
                    tooltipLeft: coords.x,
                  });
                }}
                onMouseLeave={hideTooltip}
              />
            ))}
            <AxisBottom top={height - margin.bottom} scale={xScaleAge} />
            <AxisLeft scale={yScaleAge} />
          </Group>
        </svg>
      </div>

      {tooltipData !== undefined && (
        <Tooltip top={tooltipTop} left={tooltipLeft}>
          <div>{`Anzahl: ${tooltipData}`}</div>
        </Tooltip>
      )}
    </div>
  );
};

export default MyCharts2;
