import ApiService from "../Application/ApiService";

/**
 * Hilfsfunktion, um Personen aus verschiedenen Endpunkten abzurufen und zu einer Liste zusammenzuführen.
 * @returns {Promise<Array>} - Eine Liste von Personen.
 */
export const fetchAllPersons = async () => {
    try {
        // Daten aus verschiedenen Endpunkten abrufen
        const vermittler = await ApiService.fetchVermittler(); // Vermutlich `/api/vermittler`
        console.log("Vermittler Response:", vermittler);

        const einbringer = await ApiService.fetchEinbringer(); // Vermutlich `/api/einbringer`
        console.log("Einbringer Response:", einbringer);

        const betreuer = await ApiService.fetchBetreuer(); // Vermutlich `/api/betreuer`
        console.log("Betreuer Response:", betreuer);

        // Alle Personen zusammenführen
        const allPersons = [
            ...sanitizePersons(vermittler, "Vermittler"),
            ...sanitizePersons(einbringer, "Einbringer"),
            ...sanitizePersons(betreuer, "Betreuer"),
        ];

        console.log("Zusammengeführte Personenliste:", allPersons);

        return allPersons;
    } catch (error) {
        console.error("Fehler beim Abrufen der Personen:", error);
        throw error;
    }
};

/**
 * Bereinigt und markiert Personen aus einer bestimmten Quelle.
 * @param {Array} persons - Die ursprüngliche Personenliste.
 * @param {string} type - Der Typ der Quelle (z. B. "Vermittler", "Einbringer", "Betreuer").
 * @returns {Array} - Die bereinigte Personenliste.
 */
const sanitizePersons = (persons, type) => {
    if (!Array.isArray(persons)) {
        console.warn(`Erwartete ein Array, aber erhalten:`, persons);
        return [];
    }

    return persons.map(person => ({
        id: person.id,
        vorname: person.vorname,
        nachname: person.nachname,
        email: person.email?.address || "Keine Email",
        telefon: person.tel || "Keine Telefonnummer",
        adresse: `${person.adresse?.strasse || ""}, ${person.adresse?.ort || ""}, ${person.adresse?.land || ""}`,
        typ: type, // Hinzufügen des Typs (z. B. "Vermittler", "Einbringer", "Betreuer")
    }));
};
