import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image } from 'react-native';
import { Platform } from 'react-native';

// Import your image assets
import balk1 from "./images/speisekarte.png";
import lup2 from "./images/suche.png";
import haus3 from "./images/haus.png";
import user4 from "./images/nutzer.png";
import bild5 from "./images/galerie.png";
import auk6 from "./images/richter.png";
import setting7 from "./images/die-einstellungen.png";

const LeftBar = () => {
    const [hoverStates, setHoverStates] = useState(Array(7).fill(false)); // Adjusted to hold 7 buttons

    const handleHover = (index, isHovered) => {
        // Ensure we have the latest hover state for all buttons
        const updatedStates = [...hoverStates];
        updatedStates[index] = isHovered;
        setHoverStates(updatedStates);
    };

    const buttons = [balk1, lup2, haus3, user4, bild5, auk6];

    return (
        <View style={styles.container}>
            <View>
                {buttons.map((buttonLabel, index) => (
                    <TouchableOpacity
                        key={index}
                        style={[
                            styles.buttons,
                            hoverStates[index] && styles.hoveredButton,
                        ]}
                        onMouseEnter={() => handleHover(index, true)}
                        onMouseLeave={() => handleHover(index, false)}
                    >
                        <Image 
                            style={styles.images}
                            source={buttonLabel} 
                        />
                    </TouchableOpacity>
                ))}
            </View>
            <View style={styles.setdownview}>
                <TouchableOpacity
                    style={[
                        styles.buttons,
                        hoverStates[6] && styles.hoveredButton, // The last button
                    ]}
                    onMouseEnter={() => handleHover(6, true)}
                    onMouseLeave={() => handleHover(6, false)}
                >
                    <Image 
                        style={styles.images}
                        source={setting7} 
                    />
                </TouchableOpacity>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'center',
        width: 60,
        backgroundColor: 'rgba(250,228,181,1)',
        height: 900,
    },
    buttons: {
        padding: 20,
        backgroundColor: 'transparent', // Default background
    },
    hoveredButton: {
        backgroundColor: 'rgba(246,203,108,1)', // Hover effect background
    },
    images: {
        width: 22,
        height: 22,
    },
    setdownview: {
        marginTop: 410,
    },
});

export default LeftBar;
