import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, Image, TouchableOpacity, TextInput, CheckBox } from 'react-native'; // CheckBox importiert
import Nutzer from "./AlwaysActive/images/nutzer.png";

// Funktion zum Generieren zufälliger Namen
const generateRandomName = () => {
    const firstNames = [
        'Max', 'Anna', 'Liam', 'Sophia', 'Noah', 'Emma', 'Oliver', 'Ava', 
        'Ethan', 'Isabella', 'Lucas', 'Mia', 'Mason', 'Charlotte', 'Logan', 
        'Amelia', 'James', 'Harper', 'Jackson', 'Evelyn'
    ];

    const lastNames = [
        'Müller', 'Schmidt', 'Schneider', 'Fischer', 'Weber', 
        'Meyer', 'Wagner', 'Becker', 'Hoffmann', 'Schulz', 
        'Richter', 'Klein', 'Wolf', 'Schwarz', 'Zimmermann',
        'Braun', 'Krüger', 'Hofmann', 'Schmitz', 'Schulte'
    ];

    const firstName = firstNames[Math.floor(Math.random() * firstNames.length)];
    const lastName = lastNames[Math.floor(Math.random() * lastNames.length)];

    return { firstName, lastName };
};

// Generiere die Personenliste
const personsList = Array.from({ length: 100 }, () => generateRandomName());

const Persons = () => {
    const [searchText, setSearchText] = useState('');
    const [searchByFirstName, setSearchByFirstName] = useState(true);
    const [searchByLastName, setSearchByLastName] = useState(false);
    const [searchByBoth, setSearchByBoth] = useState(false);
    const [showFilters, setShowFilters] = useState(false); // Steuert das Dropdown-Menü

    // Funktion zum Filtern der Personenliste basierend auf den Suchkriterien
    const filteredPersons = personsList.filter(person => {
        const fullName = `${person.firstName} ${person.lastName}`.toLowerCase();
        if (searchByFirstName) {
            return person.firstName.toLowerCase().includes(searchText.toLowerCase());
        } else if (searchByLastName) {
            return person.lastName.toLowerCase().includes(searchText.toLowerCase());
        } else if (searchByBoth) {
            return fullName.includes(searchText.toLowerCase());
        }
        return true; // Wenn kein Kontrollkästchen aktiv ist, alle Personen anzeigen
    });

    return (
        <View style={styles.container}>
            <View style={{flexDirection:"row"}}>
            <Text style={styles.welcomeText}>Personen</Text>

            {/* Suchleiste mit Dropdown-Filter */}
            <View style={styles.searchContainer}>
                <TextInput
                    style={styles.searchBar}
                    placeholder="Search Personen"
                    value={searchText}
                    onChangeText={text => setSearchText(text)}
                />
                <TouchableOpacity style={styles.filterButton} onPress={() => setShowFilters(!showFilters)}>
                    <Text style={styles.filterButtonText}>Filter</Text>
                </TouchableOpacity>
            </View>
            </View>

            {/* Dropdown-Menü für Filter */}
            {showFilters && (
                <View style={styles.dropdown}>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByFirstName}
                            onValueChange={() => {
                                setSearchByFirstName(!searchByFirstName);
                                if (searchByFirstName) {
                                    setSearchByLastName(false);
                                    setSearchByBoth(false);
                                }
                            }}
                        />
                        <Text>Search by First Name</Text>
                    </View>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByLastName}
                            onValueChange={() => {
                                setSearchByLastName(!searchByLastName);
                                if (searchByLastName) {
                                    setSearchByFirstName(false);
                                    setSearchByBoth(false);
                                }
                            }}
                        />
                        <Text>Search by Last Name</Text>
                    </View>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByBoth}
                            onValueChange={() => {
                                setSearchByBoth(!searchByBoth);
                                if (searchByBoth) {
                                    setSearchByFirstName(false);
                                    setSearchByLastName(false);
                                }
                            }}
                        />
                        <Text>Search by Both</Text>
                    </View>
                </View>
            )}

            {/* Personenliste */}
            <ScrollView contentContainerStyle={styles.scrollViewContent}>
                <View style={styles.personGrid}>
                    {filteredPersons.map((person, index) => (
                        <View key={index} style={styles.personBox}>
                            <TouchableOpacity>
                                <View style={{ margin: 10 }}>
                                    <Image style={styles.images} source={Nutzer} />
                                </View>
                                <View style={{ backgroundColor: "lightgrey", height: 2, width: "100%", marginTop: 4, marginBottom: 4 }}></View>
                                <View>
                                    <Text style={styles.personName}>{person.firstName}</Text>
                                    <Text style={styles.personName}>{person.lastName}</Text>
                                </View>
                            </TouchableOpacity>
                        </View>
                    ))}
                </View>
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: '#fff',
        alignItems: 'flex-start',
    },
    welcomeText: {
        color: 'rgba(240,175,77,1)',
        fontWeight: "bold",
        fontSize: 50,
        marginBottom: 20,
    },
    searchContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 20,
        marginLeft: 150,
        width: '30%',
    },
    searchBar: {
        flex: 1,
        padding: 10,
        borderColor: 'gray',
        borderWidth: 1,
        borderRadius: 5,

    },
    filterButton: {
        marginLeft: 10,
        padding: 10,
        backgroundColor: 'rgba(240,175,77,1)',
        borderRadius: 5,
    },
    filterButtonText: {
        color: '#fff',
    },
    dropdown: {
        position: 'absolute', // Um das Layout nicht zu verschieben
        top: 60, // Position unter der Suchleiste (bei Bedarf anpassen)
        left: 20, // Position, um mit der Suchleiste auszurichten
        right: 20, // Position, um mit der Suchleiste auszurichten
        backgroundColor: '#f0f0f0',
        borderRadius: 5,
        padding: 10,
        elevation: 5,
        width: "15%"
    },
    checkBoxItem: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 10,
    },
    scrollViewContent: {
        alignItems: 'center',
    },
    personGrid: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
    },
    personBox: {
        width: '15%',
        borderWidth: 1,
        borderColor: 'lightgray',
        borderRadius: 5,
        padding: 2,
        marginBottom: 10,
        alignItems: 'center',
        justifyContent: 'center',
        height: 150, // Höhe der Kästen
        margin: 1, // Abstand zwischen Kästen
    },
    personName: {
        fontSize: 14,
    },
    images: {
        width: 40,
        height: 40,
    },
});

export default Persons;
