import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';
import { Platform } from 'react-native';

const LeftBar = () => {
    const [hoverStates, setHoverStates] = useState([false, false, false]);

    const handleHover = (index, isHovered) => {
        const updatedStates = [...hoverStates];
        updatedStates[index] = isHovered;
        setHoverStates(updatedStates);
    };

    const buttons = ['Button 1', 'Button 2', 'Button 3',"Button 4","Button 5","Button 6"];

    return (
        <View style={styles.container}>
            {buttons.map((buttonLabel, index) => (
                <TouchableOpacity
                    key={index}
                    style={[
                        styles.buttons,
                        hoverStates[index] && styles.hoveredButton,
                    ]}
                    onMouseEnter={() => handleHover(index, true)}
                    onMouseLeave={() => handleHover(index, false)}
                >
                    <Text>{buttonLabel}</Text>
                </TouchableOpacity>
            ))}
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flexDirection: 'column',
        justifyContent: 'top',
        alignItems: 'center',
        width: 80,
        backgroundColor: 'rgba(250,228,181,1)',
        height: 900,
    },
    buttons: {

        padding: 10,
        marginBottom: 5,
        marginTop: 10,
        backgroundColor: 'transparent', // Default background
    },
    hoveredButton: {
        backgroundColor: 'rgba(246,203,108,1)', // Hover effect background
    },
});

export default LeftBar;
