import ApiService from "../Application/ApiService";

/**
 * Führt eine Fetch-Funktion aus, protokolliert die Antwort und behandelt Fehler.
 * @param {string} type - Der Typ der Datenquelle (z. B. "Vermittler").
 * @param {Function} fetchFunction - Die Fetch-Funktion für die API.
 * @returns {Promise<Array>} - Die bereinigte API-Antwort oder ein leeres Array.
 */
const fetchAndLog = async (type, fetchFunction) => {
    try {
        const response = await fetchFunction();

        if (!response) {
            console.warn(`${type} API hat keine Daten zurückgegeben.`);
            return [];
        }

        console.log(`Raw ${type} response:`, response);

        const sanitizedResponse = sanitizeData(response);
        console.log(`Sanitized ${type} data:`, sanitizedResponse);

        return sanitizedResponse;
    } catch (error) {
        console.error(`Fehler beim Abrufen von ${type}:`, error);
        return [];
    }
};

/**
 * Entfernt zirkuläre Referenzen und verschachtelte Strukturen.
 * @param {Object} data - Die zu bereinigenden Daten.
 * @param {Set} seen - Set, um bereits besuchte Objekte zu verfolgen.
 * @returns {Object} - Bereinigte Daten.
 */
const sanitizeData = (data, seen = new WeakSet()) => {
    if (data && typeof data === 'object') {
        if (seen.has(data)) {
            return undefined; // Zirkuläre Referenzen entfernen
        }
        seen.add(data);

        const sanitized = Array.isArray(data) ? [] : {};
        for (const key in data) {
            if (key === 'betreuer' || key === 'einbringer' || key === 'vermittler') {
                sanitized[key] = undefined; // Tiefe Referenzen entfernen
            } else {
                sanitized[key] = sanitizeData(data[key], seen);
            }
        }
        return sanitized;
    }
    return data;
};

/**
 * Ruft alle Personen aus verschiedenen Endpunkten ab und führt sie zu einer Liste zusammen.
 * @returns {Promise<Array>} - Eine zusammengeführte Liste von Personen.
 */
export const fetchAllPersons = async () => {
    try {
        const vermittler = await fetchAndLog("Vermittler", ApiService.fetchVermittler);
        const einbringer = await fetchAndLog("Einbringer", ApiService.fetchEinbringer);
        const betreuer = await fetchAndLog("Betreuer", ApiService.fetchBetreuer);

        const allPersons = [
            ...sanitizePersons(vermittler, "Vermittler"),
            ...sanitizePersons(einbringer, "Einbringer"),
            ...sanitizePersons(betreuer, "Betreuer"),
        ];

        console.log("Zusammengeführte Personenliste:", allPersons);
        return allPersons;
    } catch (error) {
        console.error("Fehler beim Abrufen der Personen:", error);
        throw error;
    }
};

/**
 * Bereinigt und formatiert die Liste der Personen aus einer bestimmten Quelle.
 * @param {Array} persons - Die ursprüngliche Personenliste.
 * @param {string} type - Der Typ der Quelle (z. B. "Vermittler", "Einbringer", "Betreuer").
 * @returns {Array} - Die bereinigte und formattierte Personenliste.
 */
const sanitizePersons = (persons, type) => {
    if (!Array.isArray(persons)) {
        console.warn(`Erwartete ein Array, aber erhalten:`, persons);
        return [];
    }

    return persons.map(person => ({
        id: person.id,
        vorname: person.vorname || "Unbekannt",
        nachname: person.nachname || "Unbekannt",
        email: person.email?.address || "Keine Email",
        telefon: person.tel || "Keine Telefonnummer",
        adresse: [
            person.adresse?.strasse || "",
            person.adresse?.ort || "",
            person.adresse?.land || "",
        ].filter(Boolean).join(", "),
        typ: type,
    }));
};
