import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { View, Text, StyleSheet, TouchableOpacity, Image } from 'react-native';

const PersonDetail = ({ persons }) => {
    const { id } = useParams(); // ID aus der URL
    const [person, setPerson] = useState(null);
    const [imageUri, setImageUri] = useState('https://via.placeholder.com/250');
    const [selectedTab, setSelectedTab] = useState('Allgemein'); // Initialer Tab
    const [error, setError] = useState(null);

    useEffect(() => {
        if (!persons || persons.length === 0) {
            setError('Die Personenliste ist leer oder nicht definiert.');
            return;
        }

        const foundPerson = persons.find((p) => p.personenNr?.toString() === id);
        if (!foundPerson) {
            setError(`Keine Person mit der ID ${id} gefunden.`);
        } else {
            setPerson(foundPerson);
        }
    }, [id, persons]);

    const renderContent = () => {
        if (!person) {
            return <Text style={styles.error}>Keine Details verfügbar.</Text>;
        }

        return (
            <View>
                <Text style={styles.header}>Details zu {person.vorname} {person.nachname}</Text>
                <Text>Personen-Nummer: {person.personenNr}</Text>
                {/* Weitere Felder können hier hinzugefügt werden */}
            </View>
        );
    };

    if (error) {
        return (
            <View style={styles.container}>
                <Text style={styles.error}>{error}</Text>
            </View>
        );
    }

    return (
        <View style={styles.container}>
            <TouchableOpacity onPress={() => console.log('Bild hochladen')}>
                <Image source={{ uri: imageUri }} style={styles.image} />
            </TouchableOpacity>
            {renderContent()}
        </View>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20, backgroundColor: '#fff', height: '100vh' },
    error: { color: 'red', fontSize: 20, textAlign: 'center' },
    header: { fontSize: 24, fontWeight: 'bold', marginBottom: 10 },
    image: { width: 250, height: 250, borderWidth: 2, borderColor: '#ddd', marginBottom: 20 },
});

export default PersonDetail;
