import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, Picker, ActivityIndicator } from "react-native";
import * as d3 from "d3";
import ApiService from "../Application/ApiService";
const Graphs = () => {
  const [data, setData] = useState([]);
  const [chartType, setChartType] = useState("bar");
  const [metric, setMetric] = useState("sales");
  const [view, setView] = useState("date");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch the data
    async function fetchStatistics() {
      try {
        const stats = await ApiService.fetchEinbringer(); // Daten von der API abrufen
        setData(stats);
      } catch (error) {
        console.error("Fehler beim Abrufen der Statistiken:", error);
      } finally {
        setLoading(false);
      }
    }

    fetchStatistics();
  }, []);

  const aggregateData = (data, key) => {
    return d3.group(data, (d) => d[key]);
  };

  const renderChart = () => {
    if (loading) {
      return <ActivityIndicator size="large" color="#007bff" />;
    }

    if (!data.length) {
      return <Text style={styles.error}>Keine Daten verfügbar.</Text>;
    }

    const groupedData = Array.from(aggregateData(data, view)).map(([key, values]) => ({
      label: key,
      value: d3.sum(values, (v) => v[metric]),
    }));

    switch (chartType) {
      case "bar":
        return <BarChart data={groupedData} />;
      case "pie":
        return <PieChart data={groupedData} />;
      case "line":
        return <LineChart data={groupedData} />;
      case "heatmap":
        return <Heatmap data={groupedData} />;
      default:
        return <Text>Ungültiger Diagrammtyp</Text>;
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Auktionsstatistiken</Text>

      <View style={styles.controls}>
        <Picker
          selectedValue={chartType}
          style={styles.picker}
          onValueChange={(itemValue) => setChartType(itemValue)}
        >
          <Picker.Item label="Balkendiagramm" value="bar" />
          <Picker.Item label="Kreisdiagramm" value="pie" />
          <Picker.Item label="Liniendiagramm" value="line" />
          <Picker.Item label="Heatmap" value="heatmap" />
        </Picker>

        <Picker
          selectedValue={metric}
          style={styles.picker}
          onValueChange={(itemValue) => setMetric(itemValue)}
        >
          <Picker.Item label="Anzahl Verkäufe" value="sales" />
          <Picker.Item label="Umsatz" value="revenue" />
        </Picker>

        <Picker
          selectedValue={view}
          style={styles.picker}
          onValueChange={(itemValue) => setView(itemValue)}
        >
          <Picker.Item label="Nach Datum" value="date" />
          <Picker.Item label="Nach Kategorie" value="category" />
          <Picker.Item label="Nach Bundesland" value="state" />
        </Picker>
      </View>

      <View style={styles.chartContainer}>{renderChart()}</View>
    </View>
  );
};

// Dummy components for charts (use D3 or libraries like Victory, Recharts, or React Native SVG)
const BarChart = ({ data }) => (
  <Text style={styles.chartPlaceholder}>Balkendiagramm: {JSON.stringify(data)}</Text>
);

const PieChart = ({ data }) => (
  <Text style={styles.chartPlaceholder}>Kreisdiagramm: {JSON.stringify(data)}</Text>
);

const LineChart = ({ data }) => (
  <Text style={styles.chartPlaceholder}>Liniendiagramm: {JSON.stringify(data)}</Text>
);

const Heatmap = ({ data }) => (
  <Text style={styles.chartPlaceholder}>Heatmap: {JSON.stringify(data)}</Text>
);

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: "#f8f9fa",
    height: "100%",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
    textAlign: "center",
  },
  controls: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginBottom: 20,
  },
  picker: {
    flex: 1,
    marginHorizontal: 5,
    borderColor: "#ccc",
    borderWidth: 1,
    borderRadius: 5,
  },
  chartContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#fff",
    borderRadius: 10,
    padding: 10,
    shadowColor: "#000",
    shadowOpacity: 0.1,
    shadowOffset: { width: 0, height: 2 },
    shadowRadius: 4,
    elevation: 3,
  },
  chartPlaceholder: {
    textAlign: "center",
    color: "#6c757d",
    fontSize: 16,
  },
  error: {
    color: "red",
    textAlign: "center",
    fontSize: 18,
  },
});

export default Graphs;
