import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ScrollView, Image, TouchableOpacity, TextInput, CheckBox, ActivityIndicator } from 'react-native';
import { useNavigate } from 'react-router-dom';
import Nutzer from "./AlwaysActive/images/nutzer.png";
import Filter from "./AlwaysActive/images/filter.png";
import Squ4 from "./AlwaysActive/images/sq4.png";
import listic from "./AlwaysActive/images/listic.png";
import arrow from "./AlwaysActive/images/arrow.png";
import ApiService from '../Application/ApiService';

const Persons = () => {
    const [persons, setPersons] = useState([]); // Default to an empty array
    const [searchText, setSearchText] = useState('');
    const [searchByFirstName, setSearchByFirstName] = useState(true);
    const [searchByLastName, setSearchByLastName] = useState(false);
    const [searchByBoth, setSearchByBoth] = useState(false);
    const [showFilters, setShowFilters] = useState(false);
    const [changeListView, setListView] = useState(true);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const navigate = useNavigate();

    useEffect(() => {
        const fetchPersons = async () => {
            try {
                const fetchedPersons = await ApiService.fetchVermittler();
                if (Array.isArray(fetchedPersons)) {
                    setPersons(fetchedPersons); // Set data if it's an array
                } else {
                    console.error('Unexpected API response:', fetchedPersons);
                    setError("Ungültige Datenstruktur erhalten.");
                }
            } catch (err) {
                console.error("Fehler beim Laden der Personen:", err);
                setError("Daten konnten nicht geladen werden.");
            } finally {
                setLoading(false);
            }
        };

        fetchPersons();
    }, []);

    const filteredPersons = persons?.filter(person => {
        const fullName = `${person.vorname} ${person.nachname}`.toLowerCase();
        if (searchByFirstName) {
            return person.vorname?.toLowerCase().includes(searchText.toLowerCase());
        } else if (searchByLastName) {
            return person.nachname?.toLowerCase().includes(searchText.toLowerCase());
        } else if (searchByBoth) {
            return fullName.includes(searchText.toLowerCase());
        }
        return true;
    }) || []; // Ensure filteredPersons is always an array

    if (loading) {
        return (
            <View style={styles.loader}>
                <ActivityIndicator size="large" color="#f0af4d" />
                <Text>Daten werden geladen...</Text>
            </View>
        );
    }

    if (error) {
        return (
            <View style={styles.errorContainer}>
                <Text style={styles.errorText}>{error}</Text>
            </View>
        );
    }

    return (
        <View style={styles.container}>
            <View style={styles.header}>
                <Text style={styles.welcomeText}>Personen</Text>
                <View style={styles.searchContainer}>
                    <TouchableOpacity
                        style={[styles.filterButton, changeListView && styles.activeButton]}
                        onPress={() => setListView(true)}
                    >
                        <Image style={styles.icon} source={Squ4} />
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={[styles.filterButton, !changeListView && styles.activeButton]}
                        onPress={() => setListView(false)}
                    >
                        <Image style={styles.icon} source={listic} />
                    </TouchableOpacity>
                    <View style={styles.searchBarWrapper}>
                        <TextInput
                            style={styles.searchBar}
                            placeholder="Personen suchen"
                            value={searchText}
                            onChangeText={text => setSearchText(text)}
                        />
                        <TouchableOpacity onPress={() => setShowFilters(!showFilters)}>
                            <Image style={styles.icon} source={Filter} />
                        </TouchableOpacity>
                    </View>
                </View>
            </View>

            {showFilters && (
                <View style={styles.dropdown}>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByFirstName}
                            onValueChange={() => {
                                setSearchByFirstName(!searchByFirstName);
                                setSearchByLastName(false);
                                setSearchByBoth(false);
                            }}
                        />
                        <Text> Suche nach Vornamen</Text>
                    </View>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByLastName}
                            onValueChange={() => {
                                setSearchByLastName(!searchByLastName);
                                setSearchByFirstName(false);
                                setSearchByBoth(false);
                            }}
                        />
                        <Text> Suche nach Nachnamen</Text>
                    </View>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByBoth}
                            onValueChange={() => {
                                setSearchByBoth(!searchByBoth);
                                setSearchByFirstName(false);
                                setSearchByLastName(false);
                            }}
                        />
                        <Text> Suche nach Vor- und Nachnamen</Text>
                    </View>
                </View>
            )}

            <ScrollView contentContainerStyle={styles.scrollViewContent}>
                {changeListView ? (
                    <View style={styles.personGrid}>
                        {filteredPersons.map((person, index) => (
                            <View key={index} style={styles.personBox}>
                                <TouchableOpacity onPress={() => navigate(`/person/${person.personenNr}`)}>
                                    <View style={{ margin: 10 }}>
                                        <Image style={styles.images} source={Nutzer} />
                                    </View>
                                    <View style={styles.line}></View>
                                    <View>
                                        <Text style={styles.personName}>{person.vorname}</Text>
                                        <Text style={styles.personName}>{person.nachname}</Text>
                                    </View>
                                </TouchableOpacity>
                            </View>
                        ))}
                    </View>
                ) : (
                    <View style={styles.simpleList}>
                        {filteredPersons.map((person, index) => (
                            <View key={index} style={styles.simpleListItem}>
                                <TouchableOpacity onPress={() => navigate(`/person/${person.personenNr}`)} style={{ flexDirection: 'row', alignItems: 'center' }}>
                                    <Text style={{ fontWeight: "bold" }}>{person.nachname} {person.vorname}</Text>
                                    <Image style={{ width: 20, height: 20, marginLeft: 10 }} source={arrow} />
                                </TouchableOpacity>
                            </View>
                        ))}
                    </View>
                )}
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    // styles definition...
});

export default Persons;
