import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { View, Text, StyleSheet, TouchableOpacity, Image, TextInput } from 'react-native';
import { personList } from '../../Application/Classes/testDataGenerator';

const PersonDetail = () => {
    const { id } = useParams();
    const person = personList.find(p => p.personenNr.toString() === id);

    const [imageUri, setImageUri] = useState('https://via.placeholder.com/250');
    const [selectedTab, setSelectedTab] = useState('Allgemein'); // Initialer Tab

    // Funktion zum Hinzufügen eines eigenen Bildes (File-Input)
    const handleImageUpload = () => {
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = 'image/*';
        input.onchange = (e) => {
            const file = e.target.files[0];
            if (file) {
                const reader = new FileReader();
                reader.onload = () => setImageUri(reader.result);
                reader.readAsDataURL(file);
            }
        };
        input.click();
    };

    // Inhalt basierend auf dem ausgewählten Tab
    const renderContent = () => {
        switch (selectedTab) {
            case 'Allgemein':
                return (
                    <View style={styles.contentColumns}>
                        <View style={styles.leftColumn}>
                            {[
                                { label: "Personen-Nummer", value: person.personenNr },
                                { label: "Betreuer", value: person.betreuer || "Kein Betreuer" },
                                { label: "Korrespondenz", value: "Manuell eintragen" },
                                { label: "Sprache", value: person.sprache || "Nicht angegeben" },
                                { label: "Anrede", value: "Anrede auswählen" },
                                { label: "Titel-Extra", value: "" },
                                { label: "Titel-Vorgestellt", value: "" },
                                { label: "Vorname", value: person.vorname },
                                { label: "Nachname", value: person.nachname }
                            ].map((field, index) => (
                                <View key={index} style={styles.row}>
                                    <Text style={styles.label}>{field.label}:</Text>
                                    <TextInput
                                        style={styles.input}
                                        placeholder={field.value.toString()}
                                    />
                                </View>
                            ))}
                        </View>
                        <View style={styles.rightColumn}>
                            <View style={styles.textArea}>
                                <Text style={styles.label}>Bemerkung:</Text>
                                <TextInput
                                    style={styles.largeInput}
                                    multiline
                                    placeholder="Bemerkungen eintragen"
                                />
                            </View>
                            <View style={styles.textArea}>
                                <Text style={styles.label}>Persönliches:</Text>
                                <TextInput
                                    style={styles.largeInput}
                                    multiline
                                    placeholder="Persönliche Informationen eintragen"
                                />
                            </View>
                        </View>
                    </View>
                );
            case 'Kontakte':
                return <Text style={styles.tabContent}>Dies ist der Kontakte-Inhalt.</Text>;
            case 'Zuordnung':
                return <Text style={styles.tabContent}>Dies ist der Zuordnung-Inhalt.</Text>;
            case 'Einbringer':
                return <Text style={styles.tabContent}>Dies ist der Einbringer-Inhalt.</Text>;
            case 'Künstler':
                return <Text style={styles.tabContent}>Dies ist der Künstler-Inhalt.</Text>;
            case 'Exponate':
                return <Text style={styles.tabContent}>Dies ist der Exponate-Inhalt.</Text>;
            case 'Verrechnung':
                return <Text style={styles.tabContent}>Dies ist der Verrechnung-Inhalt.</Text>;
            case 'USW':
                return <Text style={styles.tabContent}>Dies ist der USW-Inhalt.</Text>;
            default:
                return <Text style={styles.tabContent}>Kein Inhalt ausgewählt.</Text>;
        }
    };

    if (!person) {
        return <Text style={styles.error}>Person nicht gefunden!</Text>;
    }

    return (
        <View style={styles.container}>
            {/* Header */}
            <View style={styles.header}>
                <View style={styles.nameContainer}>
                    <Text style={styles.welcomeText}>{person.vorname}</Text>
                    <Text style={styles.welcomeText}>{person.nachname}</Text>
                </View>
                <TouchableOpacity onPress={handleImageUpload} style={styles.imageContainer}>
                    <Image source={{ uri: imageUri }} style={styles.image} />
                    <Text style={styles.imageHint}>Bild hinzufügen</Text>
                </TouchableOpacity>
            </View>

            {/* Options-Leiste */}
            <View style={styles.optionsBar}>
                {["Allgemein", "Kontakte", "Zuordnung", "Einbringer", "Künstler", "Exponate", "Verrechnung", "USW"].map((option, index) => (
                    <TouchableOpacity
                        key={index}
                        style={[
                            styles.optionButton,
                            selectedTab === option && styles.activeOptionButton,
                        ]}
                        onPress={() => setSelectedTab(option)}
                    >
                        <Text
                            style={[
                                styles.optionText,
                                selectedTab === option && styles.activeOptionText,
                            ]}
                        >
                            {option}
                        </Text>
                    </TouchableOpacity>
                ))}
            </View>

            {/* Dynamischer Inhalt */}
            <View style={styles.content}>
                {renderContent()}
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20, backgroundColor: '#fff', height: '100vh' },
    error: { color: 'red', fontSize: 20, textAlign: 'center' },
    header: { flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20, height: 300 },
    nameContainer: { justifyContent: 'center', flex: 1 },
    welcomeText: {
        color: 'rgba(240,175,77,1)',
        fontWeight: "bold",
        fontSize: 40,
        marginBottom: 5,
    },
    imageContainer: { alignItems: 'flex-end', justifyContent: 'center', flex: 1 },
    image: { width: 250, height: 250, borderWidth: 2, borderColor: '#ddd', backgroundColor: '#f0f0f0' },
    imageHint: { marginTop: 5, fontSize: 12, color: 'gray' },
    optionsBar: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        backgroundColor: 'rgb(247,203,105)',
        paddingVertical: 8,
        marginBottom: 15,
    },
    optionButton: {
        padding: 5,
    },
    activeOptionButton: {
        backgroundColor: 'rgb(240,175,77)',
        borderRadius: 5,
    },
    optionText: {
        color: 'black',
        fontWeight: 'bold',
    },
    activeOptionText: {
        color: 'white',
    },
    content: {
        backgroundColor: '#fff', // Weißer Hintergrund
        flex: 1,
        padding: 10,
    },
    contentColumns: {
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    leftColumn: { width: '45%' },
    rightColumn: { width: '45%' },
    row: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 10,
    },
    label: {
        fontSize: 14,
        fontWeight: 'bold',
        width: '40%',
    },
    input: {
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 5,
        padding: 5,
        width: '58%',
    },
    textArea: { marginBottom: 10 },
    largeInput: {
        height: 80,
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 5,
        textAlignVertical: 'top',
        padding: 10,
        fontSize: 14,
    },
    tabContent: {
        fontSize: 16,
        color: '#333',
        textAlign: 'center',
    },
});

export default PersonDetail;
