import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, ScrollView, Image, TouchableOpacity, TextInput, CheckBox, ActivityIndicator } from 'react-native';
import listic from "./AlwaysActive/images/listic.png";
import Filter from "./AlwaysActive/images/filter.png";
import Squ4 from "./AlwaysActive/images/sq4.png";
import arrow from "./AlwaysActive/images/arrow.png";
import ApiService from '../Application/ApiService'; // Service to handle API calls

const Exponate = () => {
    const [exponateList, setExponateList] = useState([]); // State to store fetched data
    const [searchText, setSearchText] = useState('');
    const [searchByTitle, setSearchByTitle] = useState(true);
    const [showFilters, setShowFilters] = useState(false);
    const [loading, setLoading] = useState(true); // Loading state
    const [error, setError] = useState(null); // Error state

    // Fetch exponate data from the API
    useEffect(() => {
        const fetchExponate = async () => {
            try {
                const fetchedExponate = await ApiService.fetchExponate(); // Assuming fetchExponate fetches data
                setExponateList(fetchedExponate);
            } catch (err) {
                console.error("Error fetching exponate:", err);
                setError("Failed to load exponate data.");
            } finally {
                setLoading(false);
            }
        };

        fetchExponate();
    }, []);

    // Filter exponate based on search criteria
    const filteredExponate = exponateList.filter(exponat => {
        const title = exponat.titel?.toLowerCase();
        const searchQuery = searchText.toLowerCase().trim();
        if (searchByTitle && searchQuery) {
            return title?.includes(searchQuery);
        }
        return true;
    });

    if (loading) {
        return (
            <View style={styles.loader}>
                <ActivityIndicator size="large" color="#f0af4d" />
                <Text>Loading Exponate...</Text>
            </View>
        );
    }

    if (error) {
        return (
            <View style={styles.errorContainer}>
                <Text style={styles.errorText}>{error}</Text>
            </View>
        );
    }

    return (
        <View style={styles.container}>
            <View style={{ flexDirection: "row" }}>
                <Text style={styles.welcomeText}>Exponate</Text>
                <View style={styles.searchContainer}>
                    <View style={styles.searchBarWrapper}>
                        <TextInput
                            style={styles.searchBar}
                            placeholder="Search Exponate"
                            value={searchText}
                            onChangeText={text => setSearchText(text)}
                        />
                        <TouchableOpacity style={styles.filterButton} onPress={() => setShowFilters(!showFilters)}>
                            <Image style={{ width: 20, height: 20 }} source={Filter} />
                        </TouchableOpacity>
                    </View>
                </View>
            </View>

            {showFilters && (
                <View style={styles.dropdown}>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByTitle}
                            onValueChange={() => setSearchByTitle(!searchByTitle)}
                        />
                        <Text> Search by Title</Text>
                    </View>
                </View>
            )}

            <View style={styles.listHeader}>
                <Text style={[styles.listValueName, { width: 300 }]}>Exponate-ID</Text>
                <Text style={[styles.listValueName, { width: 300 }]}>Title</Text>
                <Text style={[styles.listValueName, { width: 300 }]}>Category</Text>
                <Text style={[styles.listValueName, { width: 300 }]}>Weight</Text>
            </View>

            <ScrollView contentContainerStyle={styles.scrollViewContent}>
                {filteredExponate.length > 0 ? (
                    filteredExponate.map((exponat, index) => (
                        <View key={index} style={styles.simpleListItem}>
                            <View style={{ flexDirection: "row", flex: 1 }}>
                                <Text style={{ width: 300 }}>{exponat.exponatId}</Text>
                                <Text style={{ width: 300 }}>{exponat.titel}</Text>
                                <Text style={{ width: 300 }}>{exponat.kategorie}</Text>
                                <Text style={{ width: 300 }}>{exponat.gewicht}</Text>
                            </View>
                            <TouchableOpacity>
                                <Image style={{ width: 25, height: 25 }} source={arrow} />
                            </TouchableOpacity>
                        </View>
                    ))
                ) : (
                    <Text>No Exponate found</Text>
                )}
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: '#fff',
    },
    welcomeText: {
        color: 'rgba(240,175,77,1)',
        fontWeight: "bold",
        fontSize: 50,
    },
    searchContainer: {
        marginLeft: 'auto',
    },
    searchBarWrapper: {
        flexDirection: 'row',
        borderWidth: 1,
        borderColor: 'gray',
        borderRadius: 5,
        paddingHorizontal: 10,
        alignItems: 'center',
    },
    searchBar: {
        flex: 1,
        padding: 10,
    },
    filterButton: {
        padding: 10,
    },
    dropdown: {
        borderWidth: 1,
        borderColor: 'lightgray',
        borderRadius: 5,
        padding: 10,
        marginTop: 10,
        width: 300,
    },
    checkBoxItem: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 10,
    },
    listHeader: {
        flexDirection: 'row',
        marginTop: 20,
        marginBottom: 10,
    },
    listValueName: {
        fontSize: 16,
        fontWeight: "bold",
    },
    scrollViewContent: {
        flexGrow: 1,
    },
    simpleListItem: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingVertical: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#ddd',
    },
    loader: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    errorContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    errorText: {
        color: 'red',
        fontSize: 16,
    },
});

export default Exponate;
