import React, { useEffect, useState } from "react";
import {
    View,
    Text,
    StyleSheet,
    ScrollView,
    ActivityIndicator,
    TouchableOpacity,
    TextInput,
    Image,
    CheckBox,
} from "react-native";
import { useNavigate } from "react-router-dom";
import Squ4 from "./AlwaysActive/images/sq4.png";
import listic from "./AlwaysActive/images/listic.png";
import Filter from "./AlwaysActive/images/filter.png";
import Nutzer from "./AlwaysActive/images/nutzer.png";
import arrow from "./AlwaysActive/images/arrow.png";
import { fetchAllPersons } from "../Application/personService";

const Persons = () => {
    const [persons, setPersons] = useState([]);
    const [filteredPersons, setFilteredPersons] = useState([]);
    const [searchText, setSearchText] = useState("");
    const [searchByFirstName, setSearchByFirstName] = useState(true);
    const [searchByLastName, setSearchByLastName] = useState(false);
    const [searchByBoth, setSearchByBoth] = useState(false);
    const [showFilters, setShowFilters] = useState(false);
    const [changeListView, setListView] = useState(true);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const loadPersons = async () => {
            try {
                const allPersons = await fetchAllPersons();
                setPersons(allPersons);
                setFilteredPersons(allPersons);
            } catch (err) {
                console.error("Fehler beim Laden der Personen:", err);
                setError("Daten konnten nicht geladen werden.");
            } finally {
                setLoading(false);
            }
        };

        loadPersons();
    }, []);

    useEffect(() => {
        const filterPersons = () => {
            const lowercasedSearchText = searchText.toLowerCase();
            const filtered = persons.filter((person) => {
                const fullName = `${person.vorname} ${person.nachname}`.toLowerCase();
                if (searchByFirstName) {
                    return person.vorname?.toLowerCase().includes(lowercasedSearchText);
                } else if (searchByLastName) {
                    return person.nachname?.toLowerCase().includes(lowercasedSearchText);
                } else if (searchByBoth) {
                    return fullName.includes(lowercasedSearchText);
                }
                return true;
            });
            setFilteredPersons(filtered);
        };

        filterPersons();
    }, [searchText, searchByFirstName, searchByLastName, searchByBoth, persons]);

    if (loading) {
        return (
            <View style={styles.loader}>
                <ActivityIndicator size="large" color="#f0af4d" />
                <Text>Daten werden geladen...</Text>
            </View>
        );
    }

    if (error) {
        return (
            <View style={styles.errorContainer}>
                <Text style={styles.errorText}>{error}</Text>
            </View>
        );
    }

    return (
        <View style={styles.container}>
            <View style={styles.header}>
                <Text style={styles.welcomeText}>Personen</Text>
                <View style={styles.searchContainer}>
                    <TouchableOpacity
                        style={[styles.filterButton, changeListView && styles.activeButton]}
                        onPress={() => setListView(true)}
                    >
                        <Image style={styles.icon} source={Squ4} />
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={[styles.filterButton, !changeListView && styles.activeButton]}
                        onPress={() => setListView(false)}
                    >
                        <Image style={styles.icon} source={listic} />
                    </TouchableOpacity>
                    <View style={styles.searchBarWrapper}>
                        <TextInput
                            style={styles.searchBar}
                            placeholder="Personen suchen"
                            value={searchText}
                            onChangeText={(text) => setSearchText(text)}
                        />
                        <TouchableOpacity onPress={() => setShowFilters(!showFilters)}>
                            <Image style={styles.icon} source={Filter} />
                        </TouchableOpacity>
                    </View>
                </View>
            </View>

            {showFilters && (
                <View style={styles.dropdown}>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByFirstName}
                            onValueChange={() => {
                                setSearchByFirstName(!searchByFirstName);
                                setSearchByLastName(false);
                                setSearchByBoth(false);
                            }}
                        />
                        <Text> Suche nach Vornamen</Text>
                    </View>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByLastName}
                            onValueChange={() => {
                                setSearchByLastName(!searchByLastName);
                                setSearchByFirstName(false);
                                setSearchByBoth(false);
                            }}
                        />
                        <Text> Suche nach Nachnamen</Text>
                    </View>
                    <View style={styles.checkBoxItem}>
                        <CheckBox
                            value={searchByBoth}
                            onValueChange={() => {
                                setSearchByBoth(!searchByBoth);
                                setSearchByFirstName(false);
                                setSearchByLastName(false);
                            }}
                        />
                        <Text> Suche nach Vor- und Nachnamen</Text>
                    </View>
                </View>
            )}

            <ScrollView contentContainerStyle={styles.scrollViewContent}>
                {changeListView ? (
                    <View style={styles.personGrid}>
                        {filteredPersons.map((person, index) => (
                            <View key={index} style={styles.personBox}>
                                <TouchableOpacity onPress={() => navigate(`/person/${person.id}`)}>
                                    <Image style={styles.images} source={Nutzer} />
                                    <View style={styles.line}></View>
                                    <Text style={styles.personName}>{person.vorname}</Text>
                                    <Text style={styles.personName}>{person.nachname}</Text>
                                    <Text style={styles.personType}>{person.typ}</Text>
                                </TouchableOpacity>
                            </View>
                        ))}
                    </View>
                ) : (
                    <View style={styles.simpleList}>
                        {filteredPersons.map((person, index) => (
                            <View key={index} style={styles.simpleListItem}>
                                <TouchableOpacity onPress={() => navigate(`/person/${person.id}`)}>
                                    <Text style={styles.listText}>
                                        <Text style={{ fontWeight: "bold" }}>{person.nachname}</Text>{" "}
                                        {person.vorname} ({person.typ}) - {person.email || "Keine Email"}
                                    </Text>
                                </TouchableOpacity>
                            </View>
                        ))}
                    </View>
                )}
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: { flex: 1, padding: 20, backgroundColor: "#fff" },
    loader: { flex: 1, justifyContent: "center", alignItems: "center" },
    errorContainer: { flex: 1, justifyContent: "center", alignItems: "center" },
    errorText: { color: "red", fontSize: 16 },
    header: { flexDirection: "row", justifyContent: "space-between", alignItems: "center" },
    welcomeText: { color: "rgba(240,175,77,1)", fontWeight: "bold", fontSize: 50 },
    searchContainer: { flexDirection: "row", alignItems: "center", marginLeft: "auto" },
    searchBarWrapper: { flexDirection: "row", alignItems: "center", borderWidth: 1, borderColor: "gray", borderRadius: 5, paddingHorizontal: 10, height: 40, marginLeft: 10 },
    searchBar: { flex: 1, paddingVertical: 5 },
    icon: { width: 20, height: 20, marginHorizontal: 5 },
    filterButton: { padding: 5 },
    activeButton: { backgroundColor: "rgba(240,175,77,1)", borderRadius: 5 },
    dropdown: { padding: 10, borderWidth: 1, borderColor: "lightgray", borderRadius: 5, marginTop: 10 },
    checkBoxItem: { flexDirection: "row", alignItems: "center", marginBottom: 10 },
    scrollViewContent: { alignItems: "center" },
    personGrid: { flexDirection: "row", flexWrap: "wrap", justifyContent: "space-between" },
    personBox: { width: "18%", marginBottom: 20, alignItems: "center" },
    images: { width: 60, height: 60, borderRadius: 30 },
    line: { backgroundColor: "lightgrey", height: 1, width: "100%", marginVertical: 5 },
    personName: { fontSize: 12, textAlign: "center" },
    personType: { fontSize: 10, color: "gray" },
    simpleList: { paddingHorizontal: 10 },
    simpleListItem: { paddingVertical: 15, borderBottomWidth: 1, borderColor: "#ddd" },
    listText: { fontSize: 14 },
});

export default Persons;
