import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { View, Text, StyleSheet, TouchableOpacity, Image, TextInput } from 'react-native';
import { personList } from '../../Application/Classes/testDataGenerator';

const PersonDetail = () => {
    const { id } = useParams();
    const person = personList.find(p => p.personenNr.toString() === id);

    const [imageUri, setImageUri] = useState('https://via.placeholder.com/250');
    const [selectedTab, setSelectedTab] = useState('Allgemein'); // Initialer Tab

    // Funktion zum Hinzufügen eines eigenen Bildes (File-Input)
    const handleImageUpload = () => {
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = 'image/*';
        input.onchange = (e) => {
            const file = e.target.files[0];
            if (file) {
                const reader = new FileReader();
                reader.onload = () => setImageUri(reader.result);
                reader.readAsDataURL(file);
            }
        };
        input.click();
    };

    // Inhalt basierend auf dem ausgewählten Tab
    const renderContent = () => {
        switch (selectedTab) {
            case 'Allgemein':
                return (
                    <View style={styles.contentColumns}>
                        <View style={styles.leftColumn}>
                            {[
                                { label: "Personen-Nummer", value: person.personenNr },
                                { label: "Betreuer", value: person.betreuer || "Kein Betreuer" },
                                { label: "Korrespondenz", value: person.korrespondenz || "Nicht angegeben" },
                                { label: "Sprache", value: person.sprache || "Nicht angegeben" },
                                { label: "Anrede", value: person.anrede || "Nicht angegeben" },
                                { label: "Vorname", value: person.vorname },
                                { label: "Nachname", value: person.nachname },
                            ].map((field, index) => (
                                <View key={index} style={styles.row}>
                                    <Text style={styles.label}>{field.label}:</Text>
                                    <Text style={styles.value}>{field.value}</Text>
                                </View>
                            ))}
                        </View>
                        <View style={styles.rightColumn}>
                            <View style={styles.textArea}>
                                <Text style={styles.label}>Bemerkung:</Text>
                                <Text style={styles.value}>{person.bemerkung || "Keine Bemerkungen"}</Text>
                            </View>
                            <View style={styles.textArea}>
                                <Text style={styles.label}>Persönliches:</Text>
                                <Text style={styles.value}>{person.persoenliches || "Keine persönlichen Informationen"}</Text>
                            </View>
                        </View>
                    </View>
                );
            case 'Kontakte':
                return (
                    <View style={styles.tabContent}>
                        {person.adressen?.map((adresse, index) => (
                            <View key={index} style={styles.row}>
                                <Text style={styles.label}>Adresse {index + 1}:</Text>
                                <Text style={styles.value}>
                                    {adresse.strasse}, {adresse.plz} {adresse.ort}, {adresse.land}
                                </Text>
                            </View>
                        ))}
                        <View style={styles.row}>
                            <Text style={styles.label}>Telefon:</Text>
                            <Text style={styles.value}>{person.telefon}</Text>
                        </View>
                        <View style={styles.row}>
                            <Text style={styles.label}>E-Mail:</Text>
                            <Text style={styles.value}>{person.email}</Text>
                        </View>
                    </View>
                );
            case 'Zuordnung':
                return (
                    <View style={styles.tabContent}>
                        <Text style={styles.label}>Betreuer:</Text>
                        <Text style={styles.value}>{person.betreuer?.vorname || "Kein Betreuer"}</Text>
                    </View>
                );
            case 'Einbringer':
                return (
                    <View style={styles.tabContent}>
                        <Text style={styles.label}>Einbringer:</Text>
                        <Text style={styles.value}>{person.einbringer || "Keine Informationen zum Einbringer"}</Text>
                    </View>
                );
            case 'Künstler':
                return (
                    <View style={styles.tabContent}>
                        <Text style={styles.label}>Künstler:</Text>
                        <Text style={styles.value}>{person.kuenstler || "Keine Künstlerinformationen"}</Text>
                    </View>
                );
            case 'Exponate':
                return (
                    <View style={styles.tabContent}>
                        {person.exponate?.map((exponat, index) => (
                            <View key={index} style={styles.row}>
                                <Text style={styles.label}>Exponat {index + 1}:</Text>
                                <Text style={styles.value}>{exponat.titel}</Text>
                            </View>
                        ))}
                    </View>
                );
            case 'Verrechnung':
                return (
                    <View style={styles.tabContent}>
                        <Text style={styles.label}>UID:</Text>
                        <Text style={styles.value}>{person.uid || "Keine UID angegeben"}</Text>
                        <Text style={styles.label}>Bank:</Text>
                        <Text style={styles.value}>{person.bank || "Keine Bankinformationen"}</Text>
                    </View>
                );
            case 'USW':
                return (
                    <View style={styles.tabContent}>
                        <Text style={styles.label}>Weitere Informationen:</Text>
                        <Text style={styles.value}>Hier können weitere Details ergänzt werden.</Text>
                    </View>
                );
            default:
                return <Text style={styles.tabContent}>Kein Inhalt ausgewählt.</Text>;
        }
    };

    if (!person) {
        return <Text style={styles.error}>Person nicht gefunden!</Text>;
    }

    return (
        <View style={styles.container}>
            {/* Header */}
            <View style={styles.header}>
                <View style={styles.nameContainer}>
                    <Text style={styles.welcomeText}>{person.vorname}</Text>
                    <Text style={styles.welcomeText}>{person.nachname}</Text>
                </View>
                <TouchableOpacity onPress={handleImageUpload} style={styles.imageContainer}>
                    <Image source={{ uri: imageUri }} style={styles.image} />
                    <Text style={styles.imageHint}>Bild hinzufügen</Text>
                </TouchableOpacity>
            </View>

            {/* Options-Leiste */}
            <View style={styles.optionsBar}>
                {["Allgemein", "Kontakte", "Zuordnung", "Einbringer", "Künstler", "Exponate", "Verrechnung", "USW"].map((option, index) => (
                    <TouchableOpacity
                        key={index}
                        style={[
                            styles.optionButton,
                            selectedTab === option && styles.activeOptionButton,
                        ]}
                        onPress={() => setSelectedTab(option)}
                    >
                        <Text
                            style={[
                                styles.optionText,
                                selectedTab === option && styles.activeOptionText,
                            ]}
                        >
                            {option}
                        </Text>
                    </TouchableOpacity>
                ))}
            </View>

            {/* Dynamischer Inhalt */}
            <View style={styles.content}>
                {renderContent()}
            </View>
        </View>
    );
};

